/// <summary>
/// Checks whether the specified record is in the <c>PCPriceExpressionRule</c> table.
/// </summary>
/// <param name="_recId">
/// The record ID of the record to find.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RefRecId _recId)
{
    return (PCPriceExpressionRule::find(_recId).RecId != 0);
}

/// <summary>
/// Finds the specified record in the <c>PCPriceExpressionRule</c> table.
/// </summary>
/// <param name="_recId">
/// The record ID of the record to find.
/// </param>
/// <param name="_forupdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>PCPriceExpressionRule</c> table; otherwise, an empty record.
/// </returns>
public static PCPriceExpressionRule find(RefRecId _recId, boolean _forupdate = false)
{
    PCPriceExpressionRule record;

    if (!_recId)
    {
        return record;
    }
    record.selectForUpdate(_forupdate);
    select firstonly record where record.RecId == _recId;
    return record;
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
