/// <summary>
/// Create or update price disc adm trans.
/// </summary>
/// <returns>
/// Error message if any.
/// </returns>
public str createOrUpdatePriceDiscAdmTrans()
{
    PriceDiscAdmTrans admTrans;
    PriceDiscAdmTable admTable;

    void updatePriceDiscAdmTrans()
    {
        admTrans.relation = this.relation;

        admTrans.AccountCode = this.AccountCode;
        admTrans.AccountRelation = this.AccountRelation;
        admTrans.initFromAccountCode();
        admTrans.ItemCode = this.ItemCode;
        admTrans.ItemRelation = this.ItemRelation;
        admTrans.initFromItemCode();
        admTrans.InventDimId = InventDim::inventDimIdBlank();

        admTrans.QuantityAmountFrom = this.QuantityAmountFrom;
        admTrans.QuantityAmountTo = this.QuantityAmountTo;
        admTrans.Percent1 = this.Percent1;
        admTrans.Percent2 = this.Percent2;
        admTrans.Markup = this.Markup;
        admTrans.CalendarDays = this.CalendarDays;
        admTrans.DeliveryTime = this.DeliveryTime;
        admTrans.DisregardLeadTime = this.DisregardLeadTime;
        admTrans.PDSCalculationId = this.PDSCalculationId;

        admTrans.Amount = this.Amount;
        admTrans.Currency = this.Currency;
        admTrans.PriceUnit = this.PriceUnit;
        admTrans.UnitId = this.UnitId;
        admTrans.SearchAgain = this.SearchAgain;
        admTrans.FromDate = this.FromDate;
        admTrans.ToDate = this.ToDate;
        admTrans.GenericCurrency = this.GenericCurrency;

        admTrans.preWrite();
        admTrans.Module = admTrans.module();
    }

    admTable = PriceDiscAdmTable::find(this.JournalNum);
    if (admTable)
    {
        if (!admTable.canUpdateLines())
        {
            this.ErrorMessage = strFmt("@REX1486", this.JournalNum);
        }
    }
    else
    {
        this.ErrorMessage = strFmt("@SYS21496", this.JournalNum);
    }

    this.selectForUpdate(true);
    if (this.ErrorMessage)
    {
        this.update();
    }
    else if (this.PriceDiscAdmTransId)
    {
        select forupdate admTrans
            where admTrans.RecId == this.PriceDiscAdmTransId;

        if (admTrans)
        {
            updatePriceDiscAdmTrans();

            if (this.verifyAndFixInventDim(admTrans) && this.verify(admTrans))
            {
                admTrans.update();
            }
            else
            {
                this.update();
            }
        }
        else
        {
            this.ErrorMessage = "@REX1313";
        }
    }
    else
    {
        admTrans.JournalNum = this.JournalNum;

        updatePriceDiscAdmTrans();

        if (this.verifyAndFixInventDim(admTrans) && this.verify(admTrans))
        {
            admTrans.insert();
            this.PriceDiscAdmTransId = admTrans.RecId;
        }

        this.update();
    }

    return this.ErrorMessage;
}

/// <summary>
/// Delete <c>PriceDiscAdmTrans</c>.
/// </summary>
public void deletePriceDiscAdmTrans()
{
    PriceDiscAdmTrans admTrans;

    delete_from admTrans where admTrans.JournalNum == this.JournalNum && admTrans.RecId == this.PriceDiscAdmTransId;
}

private boolean verify(PriceDiscAdmTrans _admTrans)
{
    boolean validated = true;
    SysInfologEnumerator enumerator;
    SysInfologMessageStruct msgStruct;
    Exception exception;
    int prefixDepth;
    str prefixText;

    #define.MyPrefix('PriceDiscAdmTransfer.verify')

    setPrefix(#MyPrefix);

    validated = _admTrans.validateWrite();

    // Convert infolog warning into this.ErrorMessage.
    if (!validated)
    {
        enumerator = SysInfologEnumerator::newData(infolog.cut());
        while (enumerator.moveNext())
        {
            msgStruct = new SysInfologMessageStruct(enumerator.currentMessage());
            exception = enumerator.currentException();

            prefixDepth = msgStruct.prefixDepth();
            if (prefixDepth)
            {
                prefixText = msgStruct.preFixTextElement(prefixDepth);
            }

            if ((exception == Exception::Warning || exception == Exception::Error) && prefixText == #MyPrefix)
            {
                this.ErrorMessage = msgStruct.message();
                break;
            }
        }
    }

    return validated;
}

private boolean anyProductDimensionSpecified()
{
    Enumerator productDims = InventProductDimension::getEnumeratorForEnabledDimensions();
    while (productDims.moveNext())
    {
        InventProductDimension productDim = productDims.current();
        FieldId priceDiscAdmTransferProductDimFieldId = productDim.fieldIdInTable(tableId2Name(this.TableId));
        if (this.(priceDiscAdmTransferProductDimFieldId))
        {
            return true;
        }
    }
    return false;
}

private boolean verifyAndFixInventDim(PriceDiscAdmTrans _admTrans)
{
    if (this.ItemCode == PriceDiscProductCodeType::Table)
    {
        if (this.anyProductDimensionSpecified())
        {
            InventTable inventTable = InventTable::find(this.ItemRelation);
            if (inventTable)
            {
                EcoResProductMaster productMaster;

                select firstonly productMaster
                    where productMaster.DisplayProductNumber == this.ItemRelation;

                if (!productMaster)
                {
                    this.ErrorMessage = "@REX1314";
                }
            }
            else
            {
                this.ErrorMessage = strFmt("@SYS5307", this.ItemRelation);
            }
        }

        if (!this.ErrorMessage)
        {
            if (this.InventSiteId || this.InventLocationId || this.anyProductDimensionSpecified())
            {
                InventDim inventDim = InventProductDimension::createInventDimFromTable(this);
                inventDim.InventSiteId = this.InventSiteId;
                inventDim.InventLocationId = this.InventLocationId;
                inventDim = InventDim::findOrCreate(inventDim);

                _admTrans.InventDimId = inventDim.inventDimId;
            }
        }
    }

    return !this.ErrorMessage;
}

private static void deleteByJournalNum(PriceDiscJournalNum _journalNum)
{
    PriceDiscAdmTransfer priceDiscAdmTransfer;

    delete_from priceDiscAdmTransfer
        where priceDiscAdmTransfer.JournalNum == _journalNum
    ;
}

/// <summary>
/// Find <c>PriceDiscAdmTransfer</c> by record ID.
/// </summary>
/// <param name="_recId">
/// Record ID.
/// </param>
/// <param name="_forUpdate">
/// Indicating whether it's for update.
/// </param>
/// <returns>
/// The record of <c>PriceDiscAdmTransfer</c> that matches the record ID.
/// </returns>
public static PriceDiscAdmTransfer find(RecId _recId, boolean _forUpdate = false )
{
    PriceDiscAdmTransfer priceDiscAdmTransfer;

    priceDiscAdmTransfer.selectForUpdate(_forUpdate);

    select firstonly priceDiscAdmTransfer
        where priceDiscAdmTransfer.RecId == _recId;

    return priceDiscAdmTransfer;
}

/// <summary>
/// Creates and initializes a <c>PriceDiscParameters</c> instance.
/// </summary>
/// <param name = "_tmpPriceDiscTrans">An <c>PriceDiscAdmTransfer</c> record.</param>
/// <returns>An instance of <c>PriceDiscParameters</c>.</returns>
protected static PriceDiscParameters createAndInitPriceDiscParameters(PriceDiscAdmTransfer _tmpPriceDiscTrans, InventDim _inventDim)
{
    PriceDiscParameters parameters = PriceDiscParameters::construct();

    parameters.parmModuleType(PriceTypeTradeAgreementMapping::newPriceTypeTradeAgreementMapping(_tmpPriceDiscTrans.relation).orderSearchModule());
    parameters.parmItemId(_tmpPriceDiscTrans.ItemRelation);
    parameters.parmInventDim(_inventDim);
    parameters.parmUnitID(_tmpPriceDiscTrans.UnitId);
    parameters.parmPriceDiscDate(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    parameters.parmQty(_tmpPriceDiscTrans.PriceUnit);
    if (_tmpPriceDiscTrans.AccountCode == PriceDiscPartyCodeType::Table)
    {
        parameters.parmAccountNum(_tmpPriceDiscTrans.AccountRelation);
    }
    parameters.parmCurrencyCode(_tmpPriceDiscTrans.Currency);

    return parameters;
}

/// <summary>
/// Creates an instance of <c>PriceDisc</c>.
/// </summary>
/// <returns>A <c>PriceDisc</c> instance.</returns>
protected static PriceDisc createPriceDiscInstance(PriceDiscParameters _parameters)
{
    return PriceDisc::newFromPriceDiscParameters(_parameters);
}

private static void fixUpCurrentPrice(PriceDiscJournalNum _journalNum)
{
    PriceDiscAdmTransfer tmpPriceDiscTrans;
    PriceDiscAdmTable admTable;
    PriceDiscAdmTrans admTrans;
    PriceDisc priceCalculator;
    PriceGroupId priceGroupId;
    CustVendAC accountId;
    InventDim inventDim;

    admTable = PriceDiscAdmTable::find(_journalNum);

    if (admTable.ExportCurrentPrice == NoYes::Yes)
    {
        ttsBegin;
        while select forUpdate tmpPriceDiscTrans
                where tmpPriceDiscTrans.JournalNum == _journalNum
                    && tmpPriceDiscTrans.ItemCode == PriceDiscProductCodeType::Table
            join admTrans where admTrans.RecId == tmpPriceDiscTrans.PriceDiscAdmTransId
            join inventDim where inventDim.inventDimId == admTrans.InventDimId
        {
            if (tmpPriceDiscTrans.AccountCode == PriceDiscPartyCodeType::GroupId)
            {
                priceGroupId = tmpPriceDiscTrans.AccountRelation;
            }

            priceCalculator = PriceDiscAdmTransfer::createPriceDiscInstance(PriceDiscAdmTransfer::createAndInitPriceDiscParameters(tmpPriceDiscTrans, inventDim));
            priceCalculator.findPrice(priceGroupId);
            tmpPriceDiscTrans.CurrentPrice = priceCalculator.price();

            if (tmpPriceDiscTrans.CurrentPrice)
            {
                tmpPriceDiscTrans.update();
            }
        }
        ttsCommit;
    }
}

