    qbdsCostModule.fetchMode(QueryFetchMode::One2One);
    qbdsCostModule.joinMode(JoinMode::OuterJoin);
    qbdsCostModule.addLink(fieldNum(PriceDiscAdmTrans, ItemRelation), fieldNum(InventTableModule, ItemId));
    qbdsCostModule.addRange(fieldNum(InventTableModule, ModuleType)).value(queryValue(ModuleInventPurchSales::Purch));
