    qbdsPriceModule.fetchMode(QueryFetchMode::One2One);
    qbdsPriceModule.joinMode(JoinMode::OuterJoin);
    qbdsPriceModule.addLink(fieldNum(PriceDiscAdmTrans, ItemRelation), fieldNum(InventTableModule, ItemId));
