    QueryBuildDataSource qbdsInventDim = qbdsAdmTrans.addDataSource(tableNum(InventDim));
    qbdsInventDim.fetchMode(QueryFetchMode::One2One);
    qbdsInventDim.joinMode(JoinMode::OuterJoin);
    qbdsInventDim.addLink(fieldNum(PriceDiscAdmTrans, InventDimId), fieldNum(InventDim, inventDimId));
    qbdsInventDim.addSelectionField(fieldNum(InventDim, InventSiteId));
    qbdsInventDim.addSelectionField(fieldNum(InventDim, InventLocationId));
    qbdsInventDim.addSelectionField(fieldNum(InventDim, InventBatchId));
    qbdsInventDim.addSelectionField(fieldNum(InventDim, WMSLocationId));
    qbdsInventDim.addSelectionField(fieldNum(InventDim, InventSerialId));

    Map targetToSourceMap = new Map(Types::String, Types::Container);

    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, PriceDiscAdmTransId),   [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, RecId)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, JournalNum),            [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, JournalNum)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, relation),              [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, relation)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, AccountCode),           [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, AccountCode)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, AccountRelation),       [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, AccountRelation)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, ItemCode),              [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, ItemCode)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, ItemRelation),          [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, ItemRelation)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, Amount),                [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, Amount)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, Currency),              [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, Currency)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, PriceUnit),             [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, PriceUnit)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, UnitId),                [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, UnitId)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, FromDate),              [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, FromDate)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, ToDate),                [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, ToDate)]);
    targetToSourceMap.insert(fieldStr(PriceDiscAdmTransfer, QuantityAmountFrom),    [qbdsAdmTrans.uniqueId(), fieldStr(PriceDiscAdmTrans, QuantityAmountFrom)]);
