/// <summary>
/// Populate the temporary table for the journal number.
/// </summary>
/// <param name="_journalNum">
/// Journal number.
/// </param>
public static void populateForJournalNum(PriceDiscJournalNum _journalNum)
{
    PriceDiscAdmTransfer::deleteByJournalNum(_journalNum);

    PriceDiscAdmTransfer::populatePriceDiscAdmTransferForJournalNum(_journalNum);

    #ISOCountryRegionCodes
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        TmpRetailPriceDiscAdmTrans tmpRetailPriceDiscAdmTrans = RetailPriceDiscResearchCalc::getDataForJournalNum(_journalNum);

        #localmacro.AdditionalDataFields
            RetailCurrent%1          = tmpRetailPriceDiscAdmTrans.%1,
            RetailCurrent%1Deviation = tmpRetailPriceDiscAdmTrans.%1Deviation,
            RetailCurrent%1Percent   = tmpRetailPriceDiscAdmTrans.%1Percent
        #endmacro

        PriceDiscAdmTransfer tmpTrans;
        update_recordSet tmpTrans
            setting #AdditionalDataFields(SalesPrice),
                    #AdditionalDataFields(BasePrice),
                    #AdditionalDataFields(InventCost),
                    #AdditionalDataFields(OtherGroupPrice),
                    RetailCurrentOrigCurBasePrice = tmpRetailPriceDiscAdmTrans.OrigCurBasePrice,
                    RetailBasePriceOrigCurrencyCode = tmpRetailPriceDiscAdmTrans.BasePriceOrigCurrencyCode
            join tmpRetailPriceDiscAdmTrans
                where tmpRetailPriceDiscAdmTrans.PriceDiscAdmTrans == tmpTrans.PriceDiscAdmTransId;
    }

    PriceDiscAdmTransfer::fixUpCurrentPrice(_journalNum);
}
