/// <summary>
/// Initializes the <c>PrintoutTable_TU</c> record by using the <c>CustVendInvoiceJour</c> record.
/// </summary>
/// <param name="_custVendInvoiceJour">
/// A table buffer that contains the <c>CustVendInvoiceJour</c> record.
/// </param>
public void initFromCustVendInvoiceJour(CustVendInvoiceJour _custVendInvoiceJour)
{
    this.CustVendAccount      = _custVendInvoiceJour.InvoiceAccount;
    this.SalesPurchId         = _custVendInvoiceJour.Num;
    this.DocumentId           = _custVendInvoiceJour.InvoiceId;
    this.DocumentDate         = _custVendInvoiceJour.InvoiceDate;
    this.CustVendNumSeqGroup  = _custVendInvoiceJour.NumberSequenceGroupId;
    this.OffSessionId         = _custVendInvoiceJour.CustVendInvoiceJour::getOffSessionId_RU();
    this.RefTableId           = _custVendInvoiceJour.TableId;
    this.RefRecId             = _custVendInvoiceJour.RecId;
    this.OriginalDocumentType = PrintoutTableOriginalDocumentType::CustInvoiceJour;

    if (_custVendInvoiceJour.TableId == tableNum(VendPackingSlipJour))
    {
        this.VendPackingSlipVersion = VendPackingSlipVersion::findLatest(_custVendInvoiceJour.RecId).RecId;
        this.OriginalDocumentType   = PrintoutTableOriginalDocumentType::VendPackingSlipJour;
    }
}

