/// <summary>
/// Retrieves the original record for the current <c>PrintoutTable_RU</c> record.
/// </summary>
/// <returns>
/// The original record.
/// </returns>
public Common originalDocument()
{
    Common originalDocument;

    switch (this.RefTableId)
    {
        case tableNum(VendPackingSlipJour):
            originalDocument = VendPackingSlipJour::findRecId(this.RefRecId);
            break;
        case tableNum(VendInvoiceJour):
            originalDocument = VendInvoiceJour::findRecId(this.RefRecId);
            break;
        case tableNum(CustPackingSlipJour):
            originalDocument = CustPackingSlipJour::findRecId(this.RefRecId);
            break;
        case tableNum(CustInvoiceJour):
            originalDocument = CustInvoiceJour::findRecId(this.RefRecId);
            break;
        case tableNum(InventTransferJour):
            originalDocument = InventTransferJour::findRecId(this.RefRecId);
            break;
        default:
            throw error("@GLS221555");
    }

    return originalDocument;
}

    public static boolean existAnyPckSlipJour(PackingSlipId     _packingSlipId,
                                                 TransDate         _packingslipDate,
                                                 Num               _purchId,
                                                 RefRecId          _vendPackingSlipVersion = 0)
{
    PrintoutTable_RU printout;
    boolean          ret;
    ;

    if (_packingSlipId && _packingslipDate && _purchId)
    {
        select firstonly RecId from printout
            where (printout.JournalType           == PrintoutTypeTable_RU::PurchReceiptM7    ||
                   printout.JournalType           == PrintoutTypeTable_RU::PurchReceiptTORG1 ||
                   printout.JournalType           == PrintoutTypeTable_RU::PurchReceiptTORG2 ||
                   printout.JournalType           == PrintoutTypeTable_RU::PurchReceiptTORG3 ||
                   printout.JournalType           == PrintoutTypeTable_RU::PurchReimbSlipM4)  &&
                  printout.DocumentId             == _packingSlipId                           &&
                  printout.DocumentDate           == _packingslipDate                         &&
                  printout.VendPackingSlipVersion == _vendPackingSlipVersion                  &&
                  printout.SalesPurchId           == _purchId;
        ret = printout.RecId ? true : false;
    }

    return ret;
}

    public static boolean existPackingSlipJour(PrintoutTypeTable_RU _typeId,
                                                  PackingSlipId        _packingSlipId,
                                                  TransDate            _packingslipDate,
                                                  Num                  _purchId,
                                                  RefRecId             _vendPackingSlipVersion = 0,
                                                  InventLocationId     _inventLocationId = "")
{
    PrintoutTable_RU printout;
    boolean          ret;
    ;

    switch (_typeId)
    {
        case PrintoutTypeTable_RU::PurchReceiptM7   :
        case PrintoutTypeTable_RU::PurchReimbSlipM4 :
        case PrintoutTypeTable_RU::PurchReceiptTORG1:
        case PrintoutTypeTable_RU::PurchReceiptTORG2:
        case PrintoutTypeTable_RU::PurchReceiptTORG3:
            if (/*_packingSlipId && */_packingslipDate && _purchId)
            {
                select firstonly RecId from printout
                    where printout.JournalType            == _typeId                 &&
                          printout.DocumentId             == _packingSlipId          &&
                          printout.DocumentDate           == _packingslipDate        &&
                          printout.SalesPurchId           == _purchId                &&
                          printout.VendPackingSlipVersion == _vendPackingSlipVersion &&
                         (prmisDefault(_inventLocationId) ||
                          printout.InventLocationId == _inventLocationId);
                ret = printout.RecId;
            }
            break;
        default                               :
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    return ret;
}

/// <summary>
/// Finds the specified record in the <c>PrintoutTable_RU</c> table.
/// </summary>
/// <param name="_recId">
/// The RecId of the PrintoutTable_RU record to find.
/// </param>
/// <param name="_forupdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
/// The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
/// A record in the <c>PrintoutTable_RU</c> table; otherwise, an empty record.
/// </returns>
public static PrintoutTable_RU find(
    RecId              _recId,
    boolean            _forupdate = false,
    ConcurrencyModel   _concurrencyModel = ConcurrencyModel::Auto)
{
    PrintoutTable_RU printoutTable;

    printoutTable.selectForUpdate(_forupdate);
    if (_forupdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        printoutTable.concurrencyModel(_concurrencyModel);
    }

    select firstonly printoutTable
        where printoutTable.RecId == _recId;

    return printoutTable;
}

/// <summary>
/// Finds the specified record in the <c>PrintoutTable_RU</c> table.
/// </summary>
/// <param name="_orderId">
/// A <c>PrintoutTableNum_RU</c> value of the PrintoutTable_RU record to find.
/// </param>
/// <param name="_typeId">
/// A <c>PrintoutTypeTable_RU</c> value of the PrintoutTable_RU record to find.
/// </param>
/// <param name="_inventLocationId">
/// An <c>InventLocationId</c> value of the PrintoutTable_RU record to find.
/// </param>
/// <param name="_forupdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>PrintoutTable_RU</c> table; otherwise, an empty record.
/// </returns>
public static PrintoutTable_RU findByJournalNum(PrintoutTableNum_RU  _orderId,
                                                PrintoutTypeTable_RU _typeId,
                                                InventLocationId     _inventLocationId,
                                                boolean              _forupdate = false)
{
    PrintoutTable_RU   printout;

    printout.selectForUpdate(_forupdate);

    switch (_typeId)
    {
        case PrintoutTypeTable_RU::PurchReceiptM7,
             PrintoutTypeTable_RU::PurchReimbSlipM4 :
            if (_orderId)
            {
                select firstonly printout
                 where printout.JournalNum       == _orderId &&
                       printout.JournalType      == _typeId  &&
                       printout.InventLocationId == _inventLocationId;
            }
        break;
        default                               :
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    return printout;
}

    public static PrintoutTable_RU findInvoiceJour(PrintoutTypeTable_RU  _typeId,
                                               InvoiceId             _invoiceId,
                                               TransDate             _invoiceDate,
                                               Num                   _salesPurchId,
                                               NumberSequenceGroupId _numSeqGroup,
                                               InventLocationId      _inventLocationId,
                                               boolean               _forupdate = false)
{
    PrintoutTable_RU   printout;
    ;

    printout.selectForUpdate(_forupdate);

    switch (_typeId)
    {
        case PrintoutTypeTable_RU::SalesInvoiceM15 :
            if (_invoiceDate && _salesPurchId)
            {
                select firstonly printout
                    where printout.JournalType         == _typeId       &&
                          printout.DocumentId          == _invoiceId    &&
                          printout.DocumentDate        == _invoiceDate  &&
                          printout.SalesPurchId        == _salesPurchId &&
                          printout.CustVendNumSeqGroup == _numSeqGroup  &&
                          printout.InventLocationId    == _inventLocationId;
            }
            break;
        default                               :
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    return printout;
}

    public static PrintoutTable_RU findPackingSlipJour(PrintoutTypeTable_RU _typeId,
                                                   PackingSlipId        _packingSlipId,
                                                   TransDate            _packingslipDate,
                                                   Num                  _purchId,
                                                   InventLocationId     _inventLocationId,
                                                   RefRecId             _vendPackingSlipVersion,
                                                   boolean              _forupdate = false)
{
    PrintoutTable_RU printout;
    ;

    printout.selectForUpdate(_forupdate);

    switch (_typeId)
    {
        case PrintoutTypeTable_RU::PurchReceiptM7   :
        case PrintoutTypeTable_RU::PurchReimbSlipM4 :
        case PrintoutTypeTable_RU::PurchReceiptTORG1:
        case PrintoutTypeTable_RU::PurchReceiptTORG2 :
        case PrintoutTypeTable_RU::PurchReceiptTORG3 :
            if (_packingSlipId && _packingslipDate && _purchId)
            {
                select firstonly printout
                    where printout.JournalType            == _typeId           &&
                          printout.DocumentId             == _packingSlipId    &&
                          printout.DocumentDate           == _packingslipDate  &&
                          printout.SalesPurchId           == _purchId          &&
                          printout.InventLocationId       == _inventLocationId &&
                          printout.VendPackingSlipVersion == _vendPackingSlipVersion;
