            break;
        default                               :
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    return printout;
}

    public static void removeProformaSlipsM4M7(VendPackingSlipJour _profJour)
{
    PrintoutTable_RU     printout;
    ;

    delete_from printout
        where (printout.JournalType  == PrintoutTypeTable_RU::PurchReceiptM7    ||
               printout.JournalType  == PrintoutTypeTable_RU::PurchReceiptTORG1 ||
               printout.JournalType  == PrintoutTypeTable_RU::PurchReceiptTORG2 ||
               printout.JournalType  == PrintoutTypeTable_RU::PurchReceiptTORG3 ||
               printout.JournalType  == PrintoutTypeTable_RU::PurchReimbSlipM4) &&
              printout.SalesPurchId  == _profJour.PurchId                       &&
              printout.DocumentDate  == _profJour.DeliveryDate                  &&
              printout.DocumentId    == _profJour.PackingSlipId                 &&
              ! printout.JournalNum;
}

public VendPackingSlipVersion VendPackingSlipVersion(VendPackingSlipVersion _relatedTable = null)
{
    if (prmIsDefault(_relatedTable))
