public void delete()
{
    ttsbegin;

    if (this.Voucher && ! this.isPosted())
    {
        if (this.numOfVoucherLines() == 0)
        {
            JournalError::deleteVoucher(tableNum(ProdJournalTable),this.JournalId,this.Voucher);
        }
    }

    super();

    ttscommit;
}

/// <summary>
/// Retrieves the string representation of the resource type.
/// </summary>
/// <returns>
/// A string representation of the resource type.
/// </returns>
display public WrkCtrTypeDisplay displayWrkCtrType()
{
    WrkCtrTypeDisplay wrkCtrTypeDisplay;

    if (this.WrkCtrId)
    {
        wrkCtrTypeDisplay = enum2str(this.wrkCtrTable().WrkCtrType);
    }

    return wrkCtrTypeDisplay;
}

public void initFromProdRouteTrans(ProdRouteTrans _prodRouteTrans)
{
    this.ProdId        = _prodRouteTrans.TransRefId;
    this.OprNum        = _prodRouteTrans.OprNum;
    this.OprPriority   = _prodRouteTrans.OprPriority;
    this.OprId         = _prodRouteTrans.OprId;
    this.WrkCtrId      = _prodRouteTrans.WrkCtrId;
    this.Worker        = _prodRouteTrans.Worker;
    this.DefaultDimension = this.copyDimension(_prodRouteTrans.DefaultDimension);
    this.JobId         = _prodRouteTrans.JobId;
    this.JobType       = _prodRouteTrans.JobType;
    this.HourPrice     = _prodRouteTrans.HourPrice;
    this.ErrorCause    = _prodRouteTrans.ErrorCause;
    this.QtyPrice      = _prodRouteTrans.QtyPrice;
    this.OprFinished   = _prodRouteTrans.OprFinished;
    this.FromTime      = _prodRouteTrans.FromTime;
    this.ToTime        = _prodRouteTrans.ToTime;
    this.JobFinished   = _prodRouteTrans.JobFinished;
    this.Cancelled     = _prodRouteTrans.Cancelled;
    this.ExecutedPct   = 0;

    if (_prodRouteTrans.TransType == ProdRouteTransType::Time)
    {
        this.CategoryHoursId    = _prodRouteTrans.CategoryId;
        this.Hours              = -_prodRouteTrans.Hours;
    }
    else
    {
        this.CategoryQtyId      = _prodRouteTrans.CategoryId;
        this.QtyGood            = -_prodRouteTrans.QtyGood;
        this.QtyError           = -_prodRouteTrans.QtyError;

        this.PdsCWQtyGood       = -_prodRouteTrans.PdsCWQtyGood;
        this.PdsCWQtyError      = -_prodRouteTrans.PdsCWQtyError;
    }
}

public void initValue()
{
    super();

    this.TransDate= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
}

public void insert()
{
    ttsbegin;

    if (! this.LineNum)
    {
        this.LineNum = ProdJournalRoute::lastLineNum(this.JournalId) + 1;
    }

    if (this.OprFinished)
    {
        this.JobFinished = NoYes::Yes;
    }

    this.updateRouteConstantRelease();

    super();

    ttscommit;
}

/// <summary>
/// Validates if the CategoryHoursId field is mandatory for this record
/// </summary>
/// <returns>
/// true if CategoryHoursId is mandatory; false otherwise
/// </returns>
/// <remarks>
/// If the corresponding time is not required to be posted, then the CategoryHoursId field is not required
/// </remarks>
public boolean isCategoryHoursMandatory()
{
    return this.isProjMethodConsumed() && this.willCostBePosted(ProdRouteTransType::Time);
}

/// <summary>
/// Validates if the CategoryQtyId field is mandatory for this record
/// </summary>
/// <returns>
/// true if CategoryQtyId is mandatory; false otherwise
/// </returns>
/// <remarks>
/// If the corresponding quantity is not required to be posted, then the CategoryQtyId field is not required
/// </remarks>
public boolean isCategoryQtyMandatory()
{
    return this.isProjMethodConsumed() && this.willCostBePosted(ProdRouteTransType::Qty);
}

public NoYes isPosted()
{
    return ProdJournalTable::isPosted(this.JournalId);
}

public boolean isProjMethodConsumed()
{
    if (!isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        return false;
    }

    return (this.prodTable().isProjMethodConsumed() ? true : false);
}

