public Hours proposalHours()
{
    #TimeConstants

    Hours               hours;

    WrkCtrTable         wrkCtrTable         = WrkCtrTable::find(this.WrkCtrId);
    CalendarId          calendarIdTransDate = wrkCtrTable.calendarIdPerDate(this.TransDate);
    CalendarId          calendarIdNextDay;

    switch (true)
    {
        case this.WrkCtrId && this.FromTime <= this.ToTime:
        hours = WorkCalendarDate::calcDefinedSeconds(
            calendarIdTransDate,
            this.TransDate,
            this.FromTime,
            this.ToTime,
            false) / #SecondsPerHour;
            break;

        case this.WrkCtrId && this.FromTime > this.ToTime:   // we assume that work has been done over midnight
            calendarIdNextDay  = wrkCtrTable.calendarIdPerDate(this.TransDate+1);

            hours = (WorkCalendarDate::calcDefinedSeconds(
            calendarIdTransDate,
            this.TransDate,
            this.FromTime,
            #secondsPerDay,
            false)
            +
            WorkCalendarDate::calcDefinedSeconds(
            calendarIdNextDay,
            this.TransDate+1,
            0,
            this.ToTime,
            false))
            / #SecondsPerHour;
            break;
    }

    return (hours > 0 ? hours : 0);
}

public Percent proposalJobExecutedPct()
{
    ProdRouteJob prodRouteJob = this.prodRouteJob();
    Percent      percent;

    if (prodRouteJob.CalcTimeHours && ! prodRouteJob.OprPriority)
    {
        percent = (this.Hours / prodRouteJob.CalcTimeHours) * 100;
    }

    return (percent > 100 ? 100 : percent);
}

public NoYes proposalJobFinished()
{
    ProdRouteJob prodRouteJob = this.prodRouteJob();

    if (prodRouteJob.ExecutedPct + this.ExecutedPct >= 100)
    {
        return NoYes::Yes;
    }

    return NoYes::No;
}

public Percent proposalRouteExecutedPct(ProdRoute _prodRoute = null)
{
    ProdRoute    prodRoute = _prodRoute;
    Percent      percent;

    if (this.OprPriority == RouteOprPriority::Primary)
    {
        if (this.JobType == RouteJobType::Setup)
        {
            if (! prodRoute.RecId)
            prodRoute  = this.prodRoute();

            if (prodRoute.CalcSetUp)
            percent = (this.Hours / prodRoute.CalcSetUp) * 100;
        }
        else
    if (this.JobType == RouteJobType::Process)
        {
            if (!prodRoute.RecId)
            prodRoute  = this.prodRoute();

            if (prodRoute.CalcProc)
            percent = (this.Hours / prodRoute.CalcProc) * 100;
        }
    }

    return (percent > 100 ? 100 : percent);
}

void setCategoryHours(RouteCostCategoryId _category)
{
    RouteCostCategory routeCostCategory= RouteCostCategory::find(_category);

    this.CategoryHoursId    = routeCostCategory.CostCategoryId;
    this.HourPrice          = routeCostCategory.costPrice(this.prodTable().inventDim().InventSiteId);
}

public void setCategoryQty(
RouteCostCategoryId _category,
Factor              _priceFactor = 0)
{
    RouteCostCategory routeCostCategory= RouteCostCategory::find(_category);

    this.CategoryQtyId      = routeCostCategory.CostCategoryId;
    this.QtyPrice           = routeCostCategory.costPrice(this.prodTable().inventDim().InventSiteId);
    if (_priceFactor)
    {
        this.QtyPrice       = this.QtyPrice * _priceFactor;
    }
}

public void update()
{
    ProdJournalRoute this_Orig = this.orig();

    if (this.ProdId != this.orig().ProdId)
    {
        throw error("@SYS23676");
    }

    if (this.OprFinished)
    {
        this.JobFinished = NoYes::Yes;
    }

    ttsbegin;

    this.updateRouteConstantRelease();

    super();

    ttscommit;
}

/// <summary>
/// Updates the <c>ConstantRelease</c> field in the <c>ProdRoute</c> table that is based on the
/// executed percentage of the journal line.
/// </summary>
public void updateRouteConstantRelease()
{
    ProdJournalRoute    orig = this.orig();
    ProdRoute           prodRoute;

    if (this.JobType == RouteJobType::Setup)
    {
        if (this.ExecutedPct == 100 && (!orig || orig.ExecutedPct < 100))
        {
            //Set the checkmark
            update_recordset prodRoute
            setting ConstantReleased = true
            where  prodRoute.ProdId      == this.ProdId
                && prodRoute.OprNum      == this.OprNum
                && prodRoute.OprPriority == this.OprPriority;
        }
        else
        {
            if (this.ExecutedPct < 100 && orig && orig.ExecutedPct == 100)
            {
                //Remove the checkmark
                update_recordset prodRoute
            setting ConstantReleased = false
            where  prodRoute.ProdId      == this.ProdId
                && prodRoute.OprNum      == this.OprNum
                && prodRoute.OprPriority == this.OprPriority;
            }
        }
    }
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean         ret;

    ret = super(_fieldIdToCheck);

    switch (_fieldIdToCheck)
    {
        case fieldNum(ProdJournalRoute, CategoryHoursId) :
            if (ret && this.isProjMethodConsumed())
                ret = ProdRoute::checkProjCategoryId(this.CategoryHoursId);
            break;
        case fieldNum(ProdJournalRoute, CategoryQtyId) :
            if (ret && this.isProjMethodConsumed())
                ret = ProdRoute::checkProjCategoryId(this.CategoryQtyId);
            break;
        //validate worker
        case fieldNum(ProdJournalRoute, Worker) :
            if (ret && this.Worker )
            {
                ret = HcmWorkerLookup::newOnlyActiveWorkersWithinCompany().validateWorker(this.Worker);
                if (!ret)
                {
                    error(strFmt("@SYS39817", HcmWorker::find(this.Worker).PersonnelNumber));
                }
            }
            break;
        case fieldNum(ProdJournalRoute, TransDate) :
            if (ret && this.TransDate)
            {
                WrkCtrResourceGroup wrkCtrResourceGroup = WrkCtrTable::resourceGroup(this.WrkCtrId, this.TransDate, this.TransDate);

                if (!wrkCtrResourceGroup)
                {
                    WrkCtrResourceGroupResource	wrkCtrResourceGroupResource;

                    select firstonly wrkCtrResourceGroupResource
                        where wrkCtrResourceGroupResource.WrkCtrId == this.WrkCtrId;

                    if(wrkCtrResourceGroupResource.RecId)
                    {
                        ret = checkFailed(strFmt("@SCM:RouteCardExceedResourceEffectiveDate", this.TransDate, this.WrkCtrId));
                    }
                }
            }
            break;

    }

    return ret;
}

/// <summary>
/// Validates changes to one field on the current record.
/// </summary>
/// <param name="_fieldName">
/// The name of the field to validate.
/// </param>
/// <param name="_arrayIndex">
/// An index that identifies the value to validate for array fields; optional.
/// </param>
/// <returns>
/// A Boolean value that indicates whether the validation passed.
/// </returns>
/// <remarks>
/// Validates that production order that the route card journal relates to matches the site of the used
/// resource group.
/// </remarks>
public boolean validateFieldValue(
FieldName _fieldName,
int       _arrayIndex = 1)
{
    boolean ret;

    ret = super(_fieldName, _arrayIndex);

    if (ret
    && _fieldName == fieldStr(ProdJournalRoute, WrkCtrId)
    && this.WrkCtrId)
    {
        // Resource has to be from the site specified on the production order
        if (this.prodTable().inventDim().InventSiteId != WrkCtrTable::resourceGroup(this.WrkCtrId, this.TransDate, this.TransDate).SiteId)
        {
            ret = checkFailed("@SYS109461");
        }
    }

    return ret;
}

public boolean validateWrite()
{
    boolean    ret;
    RouteGroup routeGroup;

    ret = super();

    if (ret && this.isProjMethodConsumed())
    {
        routeGroup = this.prodRoute().routeGroup();

        if (ret && this.Hours &&
        (((this.JobType == RouteJobType::Setup) && routeGroup.CalcSetUp) ||
        ((this.JobType == RouteJobType::Process) && routeGroup.CalcProc)))
        {
            if (ret && !this.CategoryHoursId)
            {
                ret = checkFailed(strFmt("@SYS26332", new DictField(this.TableId, fieldNum(ProdJournalRoute, CategoryHoursId)).label()));
            }
            ret = ret && ProdRoute::checkProjCategoryId(this.CategoryHoursId);
        }

        if (ret && (this.QtyGood || this.QtyError) && routeGroup.CalcQty)
        {
            if (ret && !this.CategoryQtyId)
            {
                ret = checkFailed(strFmt("@SYS26332", new DictField(this.TableId, fieldNum(ProdJournalRoute, CategoryQtyId)).label()));
            }
            ret = ret && ProdRoute::checkProjCategoryId(this.CategoryQtyId);
        }
    }
    // <GEERU>
    if (this.prodTable().useReleaseFromProduction_RU())
    {
        if (this.QtyError && !this.ReleaseKindId_RU)
        {
            ret = checkFailed(strFmt("@SYS26332", fieldId2pname(tableNum(ProdJournalRoute), fieldNum(ProdJournalRoute, ReleaseKindId_RU))));
        }

        if (!this.QtyError && this.ReleaseKindId_RU)
        {
            ret = checkFailed(strFmt("@GLS115764", this.ReleaseKindId_RU));
        }
    }
    // </GEERU>

    return ret;
}

/// <summary>
/// Validates if cost will be posted for this record
/// </summary>
/// <param name="_prodRouteTransType">
/// <c>ProdRouteTransType</c> for which we want to do validation (time or quantity)
/// </param>
/// <returns>
/// true if cost will be posted, false otherwise
/// </returns>
public boolean willCostBePosted(ProdRouteTransType _prodRouteTransType)
{
    RouteGroup routeGroup = this.prodRoute().routeGroup();

    if (_prodRouteTransType == ProdRouteTransType::Time)
    {
        if (this.JobType == RouteJobType::Setup)
        {
            return (this.Hours != 0) && routeGroup.CalcSetUp;
        }

        if (this.JobType == RouteJobType::Process)
        {
            return (this.Hours != 0) && routeGroup.CalcProc;
        }
    }

    if (_prodRouteTransType == ProdRouteTransType::Qty)
    {
        return ((this.QtyGood + this.QtyError) != 0) && routeGroup.CalcQty;
    }

    return true;
}

