public static boolean checkNotMissingPostingJob(ProdId _prodId)
{
    ProdJournalTable    prodJournalTable;
    ProdJournalRoute    prodJournalRoute;

    boolean             ok = true;

    while select forceplaceholders prodJournalRoute
    index hint ProdIdx
    group by ProdId,JournalId
    where prodJournalRoute.ProdId   == _prodId
    join TableId from prodJournalTable
    index hint JournalIdx
    where prodJournalTable.JournalId    == prodJournalRoute.JournalId       &&
          prodJournalTable.JournalType  == ProdJournalType::JobCard         &&
          prodJournalTable.Posted       == NoYes::No
    {
        ok = checkFailed(strFmt("@SYS69105",ProdJournalType::JobCard,prodJournalRoute.JournalId));
    }

    return ok;
}

public static boolean checkNotMissingPostingRoute(ProdId _prodId)
{
    ProdJournalTable    prodJournalTable;
    ProdJournalRoute    prodJournalRoute;

    boolean             ok = true;

    while select forceplaceholders prodJournalRoute
    index hint ProdIdx
    group by ProdId,JournalId
    where prodJournalRoute.ProdId   == _prodId
    join TableId from prodJournalTable
    index hint JournalIdx
    where prodJournalTable.JournalId    == prodJournalRoute.JournalId       &&
          prodJournalTable.JournalType  == ProdJournalType::RouteCard       &&
          prodJournalTable.Posted       == NoYes::No
    {
        ok = checkFailed(strFmt("@SYS69105",ProdJournalType::RouteCard,prodJournalRoute.JournalId));
    }

    return ok;
}

public static void deleteProdIdLines(ProdId _prodId)
{
    ProdJournalTable        prodJournalTable;
    ProdJournalTableData    prodJournalTableData = JournalTableData::newTable(prodJournalTable);

    ProdJournalRoute        prodJournalRoute;
    ProdJournalTransData    prodJournalTransData;

    ttsbegin;

    while select forupdate forceplaceholders prodJournalRoute
    index hint ProdIdx
    where prodJournalRoute.ProdId == _prodId
    join forupdate prodJournalTable
    index hint JournalIdx
    where prodJournalTable.JournalId    == prodJournalRoute.JournalId  &&
          prodJournalTable.Posted       == NoYes::No
    {
        if (! prodJournalTransData)
        prodJournalTransData = prodJournalTableData.journalStatic().newJournalTransData(prodJournalRoute,prodJournalTableData);

        prodJournalTransData.delete();
        prodJournalTableData.journalTable().update();
    }

    ttscommit;
}

public static ProdJournalRoute find(
ProdJournalIdRoute    _journalId,
LineNum               _lineNum,
boolean               _forUpdate = false)
{
    ProdJournalRoute prodJournalRoute;

    prodJournalRoute.selectForUpdate(_forUpdate);

    if (_journalId && _lineNum)
    select firstonly prodJournalRoute
        index hint LineIdx
        where prodJournalRoute.JournalId == _journalId &&
              prodJournalRoute.LineNum   == _lineNum;

    return prodJournalRoute;
}

public static Counter journalLineCount(ProdJournalId _prodJournalId)
{
    return any2int((select count(RecId) from prodJournalRoute
                    where prodJournalRoute.JournalId == _prodJournalId).RecId);
}

static LineNum lastLineNum(ProdJournalId _journalId)
{
    return (select maxof(LineNum) from prodJournalRoute
            where prodJournalRoute.JournalId == _journalId).LineNum;
}

