public void delete()
{
    ProdJournalBOM      prodJournalBOM;
    ProdJournalProd     prodJournalProd;
    ProdJournalRoute    prodJournalRoute;
    // <GEERU>
    ProdJournalRelease_RU   prodJournalRelease;
    // </GEERU>

    ProdJournalTable    prodJournalTable;

    ttsbegin;

    switch (this.JournalType) // Delete action not used here because lines must be deleted before the table
    {
        case ProdJournalType::Picklist:

            if (   this.ProdId
                && this.JournalId)
            {
                update_recordset prodJournalRoute
                    setting ProdPickList            = NoYes::No,
                            ProdPickListJournalId   = ''
                    where  prodJournalRoute.ProdPickListJournalId   == this.JournalId
                        && prodJournalRoute.ProdId                  == this.ProdId
                    exists join prodJournalTable
                        where prodJournalTable.JournalId    == prodJournalRoute.JournalId
                           && (prodJournalTable.JournalType == ProdJournalType::JobCard
                           ||  prodJournalTable.JournalType == ProdJournalType::RouteCard)
                           && prodJournalTable.ProdId       == this.ProdId
                           && prodJournalTable.Posted       == NoYes::No;

                update_recordset prodJournalProd
                    setting ProdPickList            = NoYes::No,
                            ProdPickListJournalId   = ''
                    where  prodJournalProd.ProdPickListJournalId   == this.JournalId
                        && prodJournalProd.ProdId                  == this.ProdId
                    exists join prodJournalTable
                        where prodJournalTable.JournalId   == prodJournalProd.JournalId
                           && prodJournalTable.JournalType == ProdJournalType::ReportFinished
                           && prodJournalTable.ProdId      == this.ProdId
                           && prodJournalTable.Posted      == NoYes::No;

                if (BOMParameters::find().EnableTrackedComponents)
                {
                    ProdAsBuiltBOMComponentLotAssociation::clearProdPickListJournalId(this.ProdId, this.JournalId);
                }
            }

            delete_from prodJournalBOM
                where prodJournalBOM.JournalId == this.JournalId;

            break;

        case ProdJournalType::ReportFinished:
            delete_from prodJournalProd
                where prodJournalProd.JournalId == this.JournalId;

            if (BOMParameters::find().EnableTrackedComponents)
            {
                ProdAsBuiltBOMProductLotAssociation::clearProdReportFinishedJournalId(this.ProdId, this.JournalId);
            }
            break;

        case ProdJournalType::JobCard,ProdJournalType::RouteCard:
            delete_from prodJournalRoute
                where prodJournalRoute.JournalId == this.JournalId;
            break;
        // <GEERU>
        case ProdJournalType::ReleaseFromProduction_RU:
            delete_from prodJournalRelease
                where prodJournalRelease.JournalId == this.JournalId;
            break;
        // </GEERU>
    }

    super();

    ttscommit;
}

/// <summary>
/// Checks if we can post the journal.
/// </summary>
/// <returns>
/// true if we can post the journal; otherwise, false.
/// </returns>
public boolean canPostJournal()
{
    if (this.RecId && this.JournalId)
    {
        return true;
    }
    return false;
}

    public void initFromProdParametersDim(ProdParametersDim _prodParametersDim)
