    this.ReducePhysical            = prodParameters.bomJournalReducePhysical;
    this.DrawNegative              = prodParameters.bomJournalDrawNegative;
    this.AcceptError               = prodParameters.JournalAcceptError;
    this.InventControlProposal     = prodParameters.InventControlProposal;

    this.AutoReportFinished        = prodParameters.AutoReportFinished;

    this.RouteAutoPickList         = prodParameters.RouteAutoPickList;
    this.ProdAutoPickList          = prodParameters.ProdAutoPickList;

    this.initFromProdParametersDim(ProdParametersDim::findDefault());

    super();
}

    public void initWaitCreateLine(
    ProdJournalNameId   _journalNameId,
    ProdId              _prodId,
    ProdJournalType     _journalType,
    ProdEndJob          _endJob,
    OprNum              _oprNum
    )
{
    ProdJournalTableData        prodJournalTableBOMData;

    prodJournalTableBOMData = JournalTableData::newTable(this);
    prodJournalTableBOMData.initBuffer(_journalNameId,_prodId,_journalType);

    this.WaitCreateLine     = NoYes::Yes;
    this.SystemBlocked      = NoYes::Yes;

    this.EndJob             = _endJob;
    this.OprNum             = _oprNum;
}

    public void insert()
{
    NumberSeq journalNum;

    ttsbegin;

    if (!this.JournalId)
    {
        journalNum     = NumberSeq::newGetNum(ProdParameters::numRefProdJournalId());
        this.JournalId = journalNum.num();
    }

    this.DrawNegative  = this.ReducePhysical ? NoYes::No : this.DrawNegative;

    super();

    ttscommit;
}

    public Counter numOfLines_RU()
{
    return any2int((select count(RecId) from prodJournalRelease_RU where prodJournalRelease_RU.JournalId == this.JournalId).RecId);
}

    public void pmfInitFromProdTable(ProdTable _prodTable)
{
    this.AcceptError = this.AcceptError || _prodTable.PmfCoByVarAllow;
}

    public void update()
{
    ProdJournalBOM      prodJournalBOM;

    if (   this.JournalId      != this.orig().JournalId
        || this.JournalNameId  != this.orig().JournalNameId
        || this.JournalType    != this.orig().JournalType)
    {
        throw error("@SYS23662");
    }

    ttsbegin;

    this.DrawNegative   = this.ReducePhysical ? NoYes::No           : this.DrawNegative;
    this.WaitCreateLine = this.SystemBlocked  ? this.WaitCreateLine : NoYes::No;

    if (    this.InventControlProposal != this.orig().InventControlProposal
        && !this.Posted)
    {
        while select forupdate prodJournalBOM
            where prodJournalBOM.JournalId             == this.JournalId
               && prodJournalBOM.InventControlProposal != this.InventControlProposal
        {
            prodJournalBOM.InventControlProposal = this.InventControlProposal;
            prodJournalBOM.update();
        }
    }

    super();

    ttscommit;
}

public display UserName userBlockIdName()
