    public boolean validateField(fieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    switch (_fieldIdToCheck)
    {
        case fieldnum(ProdJournalTable, AcceptError) :
            if (ProdTable::find(this.ProdId).PmfCoByVarAllow
                && !this.AcceptError)
            {
                ret = checkFailed(strfmt(
                        "@PRO2324",
                        fieldid2pname(
                            tablenum(ProdTable),
                            fieldnum(ProdTable, PmfCoByVarAllow)),
                        fieldid2pname(
                            tablenum(ProdJournalTable),
                            fieldnum(ProdJournalTable, AcceptError))));
            }
            break;
    }

    return ret;
}

    public boolean validateWrite(JournalTableData _journalTableData = JournalTableData::newTable(this))
{
    boolean ret = true;

    ret = _journalTableData.validateWritePre(ret);

    ret = super() && ret;

    return ret;
}

    public static boolean checkExist(ProdJournalId _journalId)
{
    if (    _journalId
        && !ProdJournalTable::exist(_journalId))
    {
        return checkFailed(strfmt(ProdJournalTable::txtNotExist(),_journalId));
    }

    return true;
}

    public static boolean checkNotMissingPosting(
    ProdId      _prodId,
    JournalId   _notJournalId
    )
{
    boolean ok = true;

    ok = ok && ProdJournalProd::checkNotMissingPosting(_prodId,_notJournalId);
    ok = ok && ProdJournalBOM::checkNotMissingPosting(_prodId);
    ok = ok && ProdJournalRoute::checkNotMissingPostingRoute(_prodId);
    ok = ok && ProdJournalRoute::checkNotMissingPostingJob(_prodId);
   // <GEERU>
    ok = ok && ProdJournalRelease_RU::checkNotMissingPosting(_prodId);
    // </GEERU>

    return ok;
}

    public static void deleteProdIdReferences(ProdId _prodId)
{
    // <GEERU>
    #isoCountryRegionCodes
    // </GEERU>

    ProdJournalTable    journalTable;

    ttsbegin;

    while select forupdate journalTable
        where journalTable.ProdId == _prodId
           && journalTable.Posted == NoYes::No
    {
        journalTable.delete();
    }

    ProdJournalBOM::deleteProdIdLines(_prodId);
    ProdJournalRoute::deleteProdIdLines(_prodId);
    ProdJournalProd::deleteProdIdLines(_prodId);
    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        ProdJournalRelease_RU::deleteProdIdLines(_prodId);
    }
    // </GEERU>

    ttscommit;
}

    static boolean exist(ProdJournalId _journalId)
{
    return    _journalId
           && (select firstonly prodJournalTable
                   where prodJournalTable.JournalId == _journalId).RecId != 0;
}

    public static ProdJournalTable find(
    ProdJournalId _journalId,
    boolean       _forUpdate = false)
{
    ProdJournalTable prodJournalTable;

    if (_journalId)
    {
        prodJournalTable.selectForUpdate(_forUpdate);

        select firstonly prodJournalTable
            where prodJournalTable.JournalId == _journalId;
    }

    return prodJournalTable;
}

/// <summary>
///  Retrieve an open picking list journal Id for a particular production order and mobile device user.
/// </summary>
/// <param name="_prodId">
///  The production order Id.
/// </param>
/// <param name="_userId">
///  The mobile device Id.
/// </param>
/// <returns>
///  Returns a journal Id.
/// </returns>
public static JournalId findOpenJournalForMobileDeviceUser(
    ProdId      _prodId,
    WHSUserId   _userId)
{
    return (select firstOnly JournalId from ProdJournalTable
                where ProdJournalTable.ProdId   == _prodId
                &&    ProdJournalTable.RFUserId == _userId
                &&    ProdJournalTable.Posted   == NoYes::No).JournalId;
}

    public static ProdJournalTable findWaitCreateLine(
    ProdId              _prodId,
    ProdJournalType     _journalType,
    ProdEndJob          _endJob,
    OprNum              _oprNum,
    boolean             _forUpdate = false
    )
{
    ProdJournalTable    prodJournalTableBOM;

    prodJournalTableBOM.selectForUpdate(_forUpdate);

    select prodJournalTableBOM
        where prodJournalTableBOM.ProdId            == _prodId
           && prodJournalTableBOM.WaitCreateLine    == NoYes::Yes
           && prodJournalTableBOM.Posted            == NoYes::No
           && prodJournalTableBOM.SystemBlocked     == NoYes::Yes
           && prodJournalTableBOM.JournalType       == _journalType
           && prodJournalTableBOM.OprNum            == _oprNum
           && prodJournalTableBOM.EndJob            == _endJob;

    return prodJournalTableBOM;
}

    public static SysInfoAction_Formrun infoActionProdJournalTable(
    ProdJournalIdBOM _prodJournalId,
    ProdJournalTable _prodJournalTable = null)
{
    SysInfoAction_Formrun   infoActionProdJournalTable;

    if (!_prodJournalTable)
    {
        _prodJournalTable = ProdJournalTable::find(_prodJournalId);
    }

    if (!_prodJournalTable)
    {
        return null;
    }

    infoActionProdJournalTable = SysInfoAction_Formrun::newFormname(formstr(ProdJournalTable));
    infoActionProdJournalTable.parmCallerBuffer(_prodJournalTable);

    return infoActionProdJournalTable;
}

    public static void initTotal(ProdJournalTable _prodJournalTable)
{
    switch (_prodJournalTable.JournalType)
    {
        case ProdJournalType::Picklist:
            _prodJournalTable.NumOfLines    = ProdJournalBOM::journalLineCount(_prodJournalTable.JournalId);
            break;
        case ProdJournalType::ReportFinished:
            _prodJournalTable.NumOfLines    = ProdJournalProd::journalLineCount(_prodJournalTable.JournalId);
            break;
        // <GEERU>
        case ProdJournalType::ReleaseFromProduction_RU  :
            _prodJournalTable.NumOfLines    = ProdJournalRelease_RU::journalLineCount(_prodJournalTable.JournalId);

            break;
        // </GEERU>
        default:
            _prodJournalTable.NumOfLines    = ProdJournalRoute::journalLineCount(_prodJournalTable.JournalId);
    }
}

    public static NoYes isPosted(JournalId _journalId)
{
    ProdJournalTable prodJournalTable = ProdJournalTable::find(_journalId);

    return    !prodJournalTable.RecId
           ||  prodJournalTable.Posted;
}

    public static JournalDescription name(ProdJournalId _journalId)
{
    return ProdJournalTable::find(_journalId).Description;
}

    public static container prodIdReferences(ProdId _prodId)
{
    ProdJournalTable        prodJournalTable;
    ProdJournalBOM          prodJournalBOM;
    ProdJournalProd         prodJournalProd;
    ProdJournalRoute        prodJournalRoute;
    // <GEERU>
    #isoCountryRegionCodes
    ProdJournalRelease_RU   prodJournalRelease;
    // </GEERU>

    Set set = new Set(Types::String);

    while select JournalId from prodJournalTable
        where prodJournalTable.ProdId == _prodId
    {
        set.add(prodJournalTable.JournalId);
    }

    while select JournalId from prodJournalBOM
        group by JournalId
        where prodJournalBOM.ProdId == _prodId
    {
        set.add(prodJournalBOM.JournalId);
    }

    while select JournalId from prodJournalProd
        group by JournalId
        where prodJournalProd.ProdId == _prodId
    {
        set.add(prodJournalProd.JournalId);
    }

    while select JournalId from prodJournalRoute
        group by JournalId
        where prodJournalRoute.ProdId == _prodId
    {
        set.add(prodJournalRoute.JournalId);
    }

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        while select JournalId from prodJournalRelease
            group by JournalId
            where prodJournalRelease.ProdId == _prodId
        {
            set.add(prodJournalRelease.JournalId);
        }
    }
    // </GEERU>

    return set.pack();
}

