/// <summary>
/// Adds quantities from a production journal transaction.
/// </summary>
/// <param name = "_journalProd">A production journal transaction.</param>
public void addQtyFromJournalTransaction(ProdJournalProd _journalProd)
{
    this.QtyGood        += _journalProd.QtyGood;
    this.QtyError       += _journalProd.QtyError;

    this.PdsCWBatchGood += _journalProd.PdsCWBatchGood;
    this.PdsCWBatchErr  += _journalProd.PdsCWBatchErr;
}

