Method.Source:
public CostAmount calcAmount()
{
    if (this.TransType== ProdRouteTransType::Qty)
    {
        return CurrencyExchangeHelper::amount((this.QtyGood + this.QtyError) * this.QtyPrice, '');
    }

    if (this.TransType== ProdRouteTransType::Time)
    {
        return CurrencyExchangeHelper::amount(this.Hours * this.HourPrice, '');
    }

    return 0;
}

