    public void initFromProdJournalRoute(ProdJournalRoute prodJournalRoute)
{
    // <GEERU>
    #ISOCountryRegionCodes
    // </GEERU>
    this.TransRefId     = prodJournalRoute.ProdId;
    this.TransRefType   = ProdTransRefType::Production;
    this.OprNum         = prodJournalRoute.OprNum;
    this.JobId          = prodJournalRoute.JobId;
    this.JobType        = prodJournalRoute.JobType;
    this.WrkCtrId       = prodJournalRoute.WrkCtrId;
    this.Worker         = prodJournalRoute.Worker;
    this.OprFinished    = prodJournalRoute.OprFinished;
    this.DefaultDimension = this.copyDimension(prodJournalRoute.DefaultDimension);
    this.OprPriority    = prodJournalRoute.OprPriority;
    this.FromTime       = prodJournalRoute.FromTime;
    this.ToTime         = prodJournalRoute.ToTime;
    this.JobFinished    = prodJournalRoute.JobFinished;
    this.ExecutedPct    = prodJournalRoute.ExecutedPct;
    this.OprId          = prodJournalRoute.OprId;
    this.Cancelled      = prodJournalRoute.Cancelled;

    this.initFromWrkCtrId(this.WrkCtrId);

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        this.StornoPhysical_RU = prodJournalRoute.StornoPhysical_RU;
    }
    // </GEERU>
}

    public void initFromProdJournalRouteHour(ProdJournalRoute prodJournalRoute)
{
    this.TransType      = ProdRouteTransType::Time;
    this.Hours          = prodJournalRoute.Hours;
    this.HourPrice      = prodJournalRoute.HourPrice;

    // Reset none hour fields
    this.QtyError       = 0;
    this.QtyGood        = 0;
    this.PdsCWQtyError  = 0;
    this.PdsCWQtyGood   = 0;

    this.QtyPrice       = 0;
    this.ErrorCause     = ProdErrorCause::None;

    if (prodJournalRoute.isProjMethodConsumed())
    {
        this.initFromProdJournalRouteProject(ProdRouteTransType::Time, prodJournalRoute);
    }
}

    public void initFromProdJournalRouteProject(
    ProdRouteTransType _type,
    ProdJournalRoute   _prodJournalRoute)
{
    ProdJournalRouteProj prodJournalRouteProj;

    if (_prodJournalRoute.isProjMethodConsumed())
    {
        prodJournalRouteProj = ProdJournalRouteProj::find(_prodJournalRoute.JournalId, _prodJournalRoute.LineNum, _type);

        this.ProjId                 = prodJournalRouteProj.ProjId;
        this.ActivityNumber         = prodJournalRouteProj.ActivityNumber;
        this.ProjLinePropertyId     = prodJournalRouteProj.ProjLinePropertyId;
        this.ProjTransId            = prodJournalRouteProj.ProjTransId;
        this.ProjSalesPrice         = prodJournalRouteProj.ProjSalesPrice;
        this.ProjSalesCurrencyId    = prodJournalRouteProj.ProjSalesCurrencyId;
        this.ProjTaxGroupId         = prodJournalRouteProj.ProjTaxGroupId;
        this.ProjTaxItemGroupId     = prodJournalRouteProj.ProjTaxItemGroupId;
        this.Txt                    = prodJournalRouteProj.ProjTaxItemGroupId;
        this.ProjTransDate          = prodJournalRouteProj.ProjTransDate;
        this.PeriodDate             = prodJournalRouteProj.PeriodDate;
    }
}

    public void initFromProdJournalRouteQty(ProdJournalRoute _prodJournalRoute)
{
    this.TransType      = ProdRouteTransType::Qty;
    this.QtyGood        = _prodJournalRoute.QtyGood;
    this.QtyError       = _prodJournalRoute.QtyError;

    this.PdsCWQtyGood   = _prodJournalRoute.PdsCWQtyGood;
    this.PdsCWQtyError  = _prodJournalRoute.PdsCWQtyError;

    this.QtyPrice       = _prodJournalRoute.QtyPrice;
    this.ErrorCause     = _prodJournalRoute.ErrorCause;

    // Reset none qty. fields
    this.Hours          = 0;
    this.HourPrice      = 0;

    if (_prodJournalRoute.isProjMethodConsumed())
    {
        this.initFromProdJournalRouteProject(ProdRouteTransType::Qty, _prodJournalRoute);
    }
}

