    public void initFromProdRoute(ProdRoute _prodRoute)
{
    this.OprPriority        = _prodRoute.OprPriority;
    this.OprId              = _prodRoute.OprId;
    this.DefaultDimension   = this.copyDimension(_prodRoute.DefaultDimension);

