    }
}

    /// <summary>
/// Sets the production unit and resource group from the resource ID.
/// </summary>
/// <param name="_wrkCtrId">
/// The ID of a resource from which to set the production unit and resource group.
/// </param>
/// <remarks>
/// This method should be called whenever the <c>WrkCtrId</c> field of this table is changed.
/// </remarks>
void initFromWrkCtrId(WrkCtrId _wrkCtrId)
{
    WrkCtrResourceGroup wrkCtrResourceGroup;
    date                dateInit = this.DateWIP;

    if (!dateInit)
    {
        dateInit = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    }

    wrkCtrResourceGroup = WrkCtrTable::resourceGroup(_wrkCtrId, dateInit, dateInit);

    this.WrkCtrId       = _wrkCtrId;
    this.ResourceGroup  = wrkCtrResourceGroup.RecId;
    this.ProdUnitId     = wrkCtrResourceGroup.ProdUnitId;
}

    public boolean isProjMethodConsumed()
{
    if (!isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        return false;
    }

    return (this.ProjId ? true : false);
}

