    public boolean mustWIPBePosted_RU()
{
    #ISOCountryRegionCodes
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoRU ]))
    {
        return !this.prodTable_RU().prodParametersDim().NoPostProdTrans_RU;
