    public ProdRoute prodRoute()
{
    ProdRoute prodRoute;

    select firstonly prodRoute
        index hint NumOprNumIdx
        where prodRoute.ProdId      == this.TransRefId &&
              prodRoute.OprNum      == this.OprNum     &&
              prodRoute.OprPriority == this.OprPriority;

    return prodRoute;
}

