    /// <summary>
/// Display method used to display the route transaction reference.
/// </summary>
/// <returns>
/// A string that contains either the production transaction reference or the plan description.
/// </returns>
public display Name transRefName()
{
    if (this.TransRefType == ProdTransRefType::Production)
    {
        if (!hasFieldAccess(tableNum(ProdTable), fieldNum(ProdTable, Name)))
        {
            return "";
        }
        return ProdTable::find(this.TransRefId).Name;
    }
    else
    {
        if (!hasFieldAccess(tableNum(PlanReference), fieldNum(PlanReference, PlanDescription)))
        {
            return "";
        }
        return PlanReference::findPlanName(this.TransRefId).PlanDescription;
    }
}

    public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    switch (_fieldIdToCheck)
    {
        case fieldNum(ProdRouteTrans, Worker) :
            if (ret && this.Worker)
            {
                ret = HcmWorkerLookup::newOnlyActiveWorkersWithinCompany().validateWorker(this.Worker);
                if (!ret)
                {
                    error(strFmt("@SYS39817", HcmWorker::find(this.Worker).PersonnelNumber));
                }
            }
            break;
    }

    return ret;
}

    /// <summary>
/// Gets the error quantity reported for the production.
/// </summary>
/// <param name="_prodId">
/// The ID of the production.
/// </param>
/// <returns>
/// An error quantity value in catch weight units.
/// </returns>
public static PdsCWInventQty cwQtyErrorProd(ProdId _prodId)
{
    ProdRouteTrans prodRouteTrans;

    select sum(PdsCWQtyError) from prodRouteTrans
        index hint ProdJobTypeIdx
        where prodRouteTrans.TransRefId   == _prodId
           && prodRouteTrans.TransRefType == ProdTransRefType::Production
           && prodRouteTrans.TransType    == ProdRouteTransType::Qty
           && prodRouteTrans.OprPriority  == RouteOprPriority::Primary;

    return prodRouteTrans.PdsCWQtyError;
}

    public static Hours hoursJob(
    ProdId       prodId,
    ProdJobId    jobId
    )
{
    ProdRouteTrans prodRouteTrans;

    select sum(Hours) from prodRouteTrans
        index hint ProdJobTypeIdx
        where prodRouteTrans.TransRefId   == prodId                      &&
              prodRouteTrans.TransRefType == ProdTransRefType::Production &&
              prodRouteTrans.TransType    == ProdRouteTransType::Time    &&
              prodRouteTrans.JobId        == jobId;

    return prodRouteTrans.Hours;
}

    public static Hours hoursOpr(
    ProdId            _prodId,
    OprNum            _oprNum,
    RouteOprPriority  _oprPriority,
    RouteJobType      _routeJobType = RouteJobType::QueueBefore
    )
{
    ProdRouteTrans  prodRouteTrans;
    boolean         sumAllJobTypes = prmisDefault(_routeJobType);

    select sum(Hours) from prodRouteTrans
        index hint ProdJobTypeIdx
        where prodRouteTrans.TransRefId   == _prodId                      &&
              prodRouteTrans.TransRefType == ProdTransRefType::Production &&
              prodRouteTrans.TransType    == ProdRouteTransType::Time     &&
              prodRouteTrans.OprNum       == _oprNum                      &&
              prodRouteTrans.OprPriority  == _oprPriority                 &&
              (sumAllJobTypes || prodRouteTrans.JobType == _routeJobType);

    return prodRouteTrans.Hours;
}

