/// <summary>
/// Queries the total good quantity started by the worker on a job.
/// </summary>
/// <param name = "_prodId"> Production ID </param>
/// <param name = "_jobId"> Job ID.</param>
/// <param name = "_worker"> Worker recID </param>
/// <returns>
/// Returns the total good quantity started by the worker on a production job.
/// </returns>
[Hookable(false)]
internal static ProdReportedGood qtyGoodJobByWorker(
ProdId       _prodId,
ProdJobId    _jobId, 
JmgWorkerRecId _worker
)
{
    if(_worker == 0 || _prodId == '')
    {
        return 0;
    }

    ProdRouteTrans prodRouteTrans;

    select sum(QtyGood) from prodRouteTrans
    where prodRouteTrans.TransRefId   == _prodId                      &&
          prodRouteTrans.TransRefType == ProdTransRefType::Production &&
          prodRouteTrans.TransType    == ProdRouteTransType::Qty      &&
          prodRouteTrans.Worker       == _worker                      &&
          prodRouteTrans.JobId        == _jobId;

    return prodRouteTrans.QtyGood;

}

/// <summary>
/// Queries the total good and error quantity reported by the worker on a job.
/// </summary>
/// <param name = "_prodId"> Production ID. </param>
/// <param name = "_jobId"> Job ID.</param>
/// <param name = "_worker"> Worker recID. </param>
/// <returns>
/// Returns the total good and error quantity reported by the worker on a production job.
/// </returns>
[Hookable(false)]
internal static ProdReportedGood qtyReportedJobByWorker(
ProdId         _prodId,
ProdJobId      _jobId,
JmgWorkerRecId _worker
)
{
    if(_worker == 0 || _prodId == '')
    {
        return 0;
    }

    ProdRouteTrans prodRouteTrans;

    select sum(QtyGood), sum(QtyError) from prodRouteTrans
    where prodRouteTrans.TransRefId   == _prodId                      &&
          prodRouteTrans.TransRefType == ProdTransRefType::Production &&
          prodRouteTrans.TransType    == ProdRouteTransType::Qty      &&
          prodRouteTrans.Worker       == _worker                      &&
          prodRouteTrans.JobId        == _jobId;

    return prodRouteTrans.QtyGood + prodRouteTrans.QtyError;

}

