/// </returns>
[Hookable(false)]
internal static ProdReportedGood qtyReportedOprByWorker(
ProdId            _prodId,
OprNum            _oprNum,
RouteOprPriority  _oprPriority,
JmgWorkerRecId    _worker
)
{
