    {
        return 0;
    }

    ProdRouteTrans prodRouteTrans;

    select sum(QtyGood), sum(QtyError) from prodRouteTrans
    where prodRouteTrans.TransRefId   == _prodId                      &&
          prodRouteTrans.TransRefType == ProdTransRefType::Production &&
          prodRouteTrans.TransType    == ProdRouteTransType::Qty      &&
          prodRouteTrans.OprNum       == _oprNum                      &&
          prodRouteTrans.Worker       == _worker                      &&
          prodRouteTrans.OprPriority  == _oprPriority;

    return prodRouteTrans.QtyGood + prodRouteTrans.QtyError;

}

/// <summary>
/// Queries the total catch weight reported quantity started by the worker on an operation.
/// </summary>
/// <param name = "_prodId"> Production ID </param>
/// <param name = "_oprNum"> Operation number </param>
/// <param name = "_oprPriority"> Operation priority </param>
/// <param name = "_worker"> Worker recID </param>
/// <returns>
/// Returns the total catch weight quantity started by the worker on an operation.
/// </returns>
[Hookable(false)]
internal static PdsCWBatchGood pdsCWQtyReportedOprByWorker(
ProdId            _prodId,
OprNum            _oprNum,
RouteOprPriority  _oprPriority,
JmgWorkerRecId    _worker
)
{
    if(_worker == 0 || _oprNum == 0 || _prodId == '')
    {
        return 0;
    }

    ProdRouteTrans prodRouteTrans;

    select sum(PdsCWQtyGood), sum(PdsCWQtyError) from prodRouteTrans
    where prodRouteTrans.TransRefId   == _prodId                      &&
          prodRouteTrans.TransRefType == ProdTransRefType::Production &&
          prodRouteTrans.TransType    == ProdRouteTransType::Qty      &&
          prodRouteTrans.OprNum       == _oprNum                      &&
          prodRouteTrans.Worker       == _worker                      &&
          prodRouteTrans.OprPriority  == _oprPriority;

    return prodRouteTrans.PdsCWQtyGood + prodRouteTrans.PdsCWQtyError;

}

    public static ProdReportedGood qtyGoodProd(ProdId _prodId)
{
    ProdRouteTrans prodRouteTrans;

    select sum(QtyGood) from prodRouteTrans
        index hint ProdJobTypeIdx
        where prodRouteTrans.TransRefId   == _prodId
           && prodRouteTrans.TransRefType == ProdTransRefType::Production
           && prodRouteTrans.TransType    == ProdRouteTransType::Qty
           && prodRouteTrans.OprNum       == ProdRoute::lastOprNum(_prodId)
           && prodRouteTrans.OprPriority  == RouteOprPriority::Primary;
    return prodRouteTrans.QtyGood;

}

/// <summary>
/// Finds a route reported good quantities at the specified operation or at a previous operation.
/// </summary>
/// <param name="_prodId">
/// The production order number.
/// </param>
/// <param name="_oprNum">
/// The operation number.
/// </param>
/// <returns>
/// The good and cw good quantity;
/// </returns>
public static container qtyGoodAtOrBeforeOprWithCWQty(
    ProdId            _prodId,
    OprNum            _oprNum)
{
    ProdRouteTrans      prodRouteTrans;
    ProdRoute           prodRoute;
    ProdReportedGood    qtyGood;
    PdsCWInventQty      cwQtyGood;
    boolean             foundResult;
    OprNum              oprNum;
    Set                 oprNumSet;
    Set                 tmpOprNumSet;
    SetEnumerator       se;

    // the initial operation
    oprNumSet = new Set(typeOf(oprNum));
    oprNumSet.add(_oprNum);

    while (   !foundResult
           && oprNumSet
           && oprNumSet.elements())
    {
        // try to find good qty for all operations at the initial operation or at the same distance from the initial operation
        // if more than one operations have a good qty then the result is the minimum good qty
        se = oprNumSet.getEnumerator();
        while (se.moveNext())
        {
            oprNum = se.current();

            select count(RecId),sum(QtyGood), sum(PdsCWQtyGood) from prodRouteTrans
                index hint ProdJobTypeIdx
                where prodRouteTrans.TransRefId   == _prodId
                   && prodRouteTrans.TransRefType == ProdTransRefType::Production
                   && prodRouteTrans.TransType    == ProdRouteTransType::Qty
                   && prodRouteTrans.OprNum       == oprNum
                   && prodRouteTrans.OprPriority  == RouteOprPriority::Primary;

            if (prodRouteTrans.RecId >= 1)
            {
                if ((!foundResult) || qtyGood > prodRouteTrans.QtyGood)
                {
                    qtyGood = prodRouteTrans.QtyGood;
                    cwQtyGood = prodRouteTrans.PdsCWQtyGood;
                }

                foundResult = true;
            }
        }

        if (!foundResult)
        {
            // find all operations that are one more step away from the initial operation
            tmpOprNumSet = new Set(oprNumSet.typeId());
            se           = oprNumSet.getEnumerator();

            while (se.moveNext())
            {
                oprNum = se.current();

                while select OprNum from prodRoute
                    where prodRoute.ProdId      == _prodId
                       && prodRoute.OprPriority == RouteOprPriority::Primary
                       && prodRoute.OprNumNext  == oprNum
                {
                    tmpOprNumSet.add(prodRoute.OprNum);
                }
            }

            oprNumSet = tmpOprNumSet;
        }
    }

    return [qtyGood, cwQtyGood];
}

/// <summary>
/// Finds a route reported error quantity at the specified operation and at a previous operations.
/// </summary>
/// <param name="_prodId">
/// The production order number.
/// </param>
/// <param name="_oprNum">
/// The operation number.
/// </param>
/// <param name="_includeCurrentOprNum">
/// true if current operation number must be included to the results; otherwise, false.
/// </param>
/// <returns>
/// The error quantity; otherwise, 0.
/// </returns>
[Hookable(false)]
internal static ProdReportedGood qtyErrorAtAndBeforeOpr(
    ProdId            _prodId,
    OprNum            _oprNum,
    boolean           _includeCurrentOprNum = true)
{
    return conPeek(ProdRouteTrans::qtyErrorAndCWQtyErrorAtAndBeforeOpr(_prodId, _oprNum, _includeCurrentOprNum), 1);
}

/// <summary>
/// Finds a route CW reported error quantity at the specified operation and at a previous operations.
/// </summary>
/// <param name="_prodId">
/// The production order number.
/// </param>
/// <param name="_oprNum">
/// The operation number.
/// </param>
/// <param name="_includeCurrentOprNum">
/// true if current operation number must be included to the results; otherwise, false.
/// </param>
/// <returns>
/// The CW error quantity; otherwise, 0.
/// </returns>
[Hookable(false)]
internal static PdsCWInventQty qtyCWErrorAtAndBeforeOpr(
    ProdId            _prodId,
    OprNum            _oprNum,
    boolean           _includeCurrentOprNum = true)
{
    return conPeek(ProdRouteTrans::qtyErrorAndCWQtyErrorAtAndBeforeOpr(_prodId, _oprNum, _includeCurrentOprNum), 2);
}

/// <summary>
/// Finds reported error quantities at the specified operation and at previous operations.
/// </summary>
/// <param name="_prodId">
/// The production order number.
/// </param>
/// <param name="_oprNum">
/// The operation number.
/// </param>
/// <param name="_includeCurrentOprNum">
/// true if current operation number must be included to the results; otherwise, false.
/// </param>
/// <returns>
/// The error and cw error quantity.
/// </returns>
[Hookable(false)]
internal static container qtyErrorAndCWQtyErrorAtAndBeforeOpr(
    ProdId            _prodId,
    OprNum            _oprNum,
    boolean           _includeCurrentOprNum = true)
{
    ProdReportedGood    qtyError;
    PdsCWInventQty      cwQtyError;

    // find all operations that are before the initial operation
    Set oprNumSet = ProdRoute::findAllPreviousOperations(_prodId, _oprNum, _includeCurrentOprNum);

    if (oprNumSet && oprNumSet.elements())
    {
        // find error qty for all operations before and the initial operation
        ProdRouteTrans      prodRouteTrans;
        SetEnumerator se = oprNumSet.getEnumerator();
        while (se.moveNext())
        {
            OprNum currentOprNum = se.current();

            select sum(QtyError), sum(PdsCWQtyError) from prodRouteTrans
                where prodRouteTrans.TransRefId   == _prodId
                   && prodRouteTrans.TransRefType == ProdTransRefType::Production
                   && prodRouteTrans.TransType    == ProdRouteTransType::Qty
                   && prodRouteTrans.OprNum       == currentOprNum
                   && prodRouteTrans.OprPriority  == RouteOprPriority::Primary;

            qtyError   += prodRouteTrans.QtyError;
            cwQtyError += prodRouteTrans.PdsCWQtyError;
        }
    }

    return [qtyError, cwQtyError];
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
public boolean shouldThrowExceptionOnZeroDelete()
{
    return true;
}

/// <summary>
/// Returns the from time with applied user's timezone offset
/// </summary>
/// <param name = "_prodRouteTrans">Route transaction for from time is required</param>
/// <returns>String conversion of from time of the route transaction with applied user's timezone offset</returns>
[SysClientCacheDataMethod(true), Hookable(false)]
internal display str displayFromTimeWithUserTimeZone(ProdRouteTrans _prodRouteTrans)
{
    return ProdTimeZoneHelper::composeTimeWithUserTimeZone(_prodRouteTrans.FromTime);
}

/// <summary>
/// Returns the to time with applied user's timezone offset
/// </summary>
/// <param name = "_prodRouteTrans">Route transaction for to time is required</param>
/// <returns>String conversion of to time of the route transaction with applied user's timezone offset</returns>
[SysClientCacheDataMethod(true), Hookable(false)]
internal display str displayToTimeWithUserTimeZone(ProdRouteTrans _prodRouteTrans)
{
    return ProdTimeZoneHelper::composeTimeWithUserTimeZone(_prodRouteTrans.ToTime);
}
