private void blankDateFields()
{
    this.InvoiceDate = dateNull();
    this.EliminationDate = dateNull();
    this.CostPaymDate = dateNull();
    this.SalesPaymDate = dateNull();
}

private boolean checkActivity()
{
    boolean ret = true;

    if (this.ActivityNumber)
    {
        ret = ProjActivityHelper::validateProjActivityParentLinkV2(this.ProjId, this.ActivityNumber);

        if (ret && !ProjParameters::allowParentActivitySelection(this.ActivityNumber))
        {
            ret = checkFailed("@Proj:ParentActivityError");
        }
    }

    return ret;
}

/// <summary>
/// Indicates whether the model that is used is locked for changes.
/// </summary>
/// <returns>
/// false if the model is locked; otherwise, true.
/// </returns>
private boolean checkModel()
{
    boolean ret = true;
    ForecastModel budgetModel = ForecastModel::find(HeadingSub::Heading, this.ModelId);

    if (!budgetModel)
    {
        ret = checkFailed(strFmt("@SYS25942", this.ModelId));
    }

    if (budgetModel.Blocked)
    {
        ret = checkFailed(strFmt("@SYS25943", budgetModel.ModelId));
    }

    return ret;
}

public boolean checkReqActivity(ProjForecastCost   projForecastCost)
{
    RequireActivity requireActivity;
    boolean ret = true;

    requireActivity = ProjTable::checkRequireActivity(projForecastCost.ProjId,tableNum(ProjForecastCost));
    if (requireActivity && (!projForecastCost.ActivityNumber))
    {
        ret = false;
    }

    return ret;
}

