{
    return ProjForecastReductionHistory::existProjForecastTransId(this.TransId);
}

boolean isProjLumpSum()
{
    ProjParameters projParameters=ProjParameters::find(false);

    if (this.ProjAllocateId && this.ProjAllocateId == projParameters.ExpenseLumpSumAllocationKey)
    {
        return true;
    }

    return false;
}

