/// <summary>
/// Merges the default dimension for the forecast line with default dimension.
/// </summary>
public void mergeResourceDimensionDefault()
{
    if (this.Resource)
    {
        DimensionDefault defaultDimension;

        if (ProjFeatureStateHelper::isProjDefaultDimensionRuleFeatureEnabled())
        {
            defaultDimension = this.DefaultDimension;
        }
        else
        {
            defaultDimension = ProjTable::find(this.ProjId).DefaultDimension;
        }

        this.DefaultDimension = this.mergeDimension(
            defaultDimension,
            ResourceFacade::getDefaultDimensionByDate(this.Resource, DateTimeUtil::newDateTime(this.StartDate, 0)));
    }
}

public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(ProjForecastCost, ProjId) :
            this.initFromProjTable(ProjTable::find(this.ProjId));
            // blank and reinitialize the date fields
            this.blankDateFields();
            this.initFromTransDate(true);
            this.mergeResourceDimensionDefault();
            break;

        case fieldNum(ProjForecastCost, CategoryId) :
            this.LinePropertyId = ProjLinePropertySetup::findLinePropertyId(this.ProjId, this.CategoryId);
            this.Txt            = ProjCategory::find(this.CategoryId).Name;
            this.setCostSalesPrice();
            break;

        case fieldNum(ProjForecastCost, Qty) :
            this.setCostSalesPrice(false);
            break;

        case fieldNum(ProjForecastCost, CostPrice) :
            this.setCostSalesPrice(false);  //set sales price only
            break;

        case fieldNum(ProjForecastCost, CurrencyId) :
        if (! this.CurrencyId)
            {
                this.CurrencyId = ProjTable::find(this.ProjId).currencyId();
            }
            // Update only the sales price based on new currency.
            this.setCostSalesPrice(false);
            this.ExchRate   = this.projTable().exchRateSales(this.StartDate,this.CurrencyId);
            break;

        case fieldNum(ProjForecastCost, StartDate) :
            this.initFromTransDate();
            break;

        case fieldNum(ProjForecastCost, InvoiceDate) :
            this.initFromInvoiceDate();
            break;

        case fieldNum(ProjForecastCost, Resource) :
            this.setResourceCategory();
            this.setCostSalesPrice();
            this.mergeResourceDimensionDefault();
            break;

        case fieldNum(ProjForecastCost, ActivityNumber) :
            this.setSchedFromDateToSourceActivity();
            break;
    }
}

public ProjTable projTable()
{
    return ProjTable::find(this.ProjId);
}

public edit smmActivityPurpose psAeditActivityPurpose(boolean _set, ProjActivityNumber _activityNumber)
{
    smmActivities smmActivities;
    str name;

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        if (_set)
        {
            this.ActivityNumber = _activityNumber;
            this.initFromProjActivity(ProjActivity::find(_activityNumber));
        }
        else
        {
            smmActivities = smmActivities::find(this.ActivityNumber);
            name = smmActivities.Purpose;
        }
    }

    return name;
}

void setCostSalesPrice(boolean _setCostPrice = true)
{
    if (! this.CurrencyId)
    {
        this.CurrencyId = ProjTable::find(this.ProjId).currencyId();
    }

    if (_setCostPrice)
    {
        ProjPriceParameters priceParams = ProjCostPriceExpense::constructPriceParametersForFindCostPrice(
            this.ProjId,
            this.Resource,
            this.CategoryId,
            this.StartDate);
        this.CostPrice = ProjCostPriceExpense::findCostPriceByPriceParameters(priceParams);
    }

    ProjPriceParameters priceParams = ProjCostSalesPrice::constructPriceParametersForFindCostSalesPrice(
        this.ProjId,
        this.Resource,
        this.CategoryId,
        this.CostPrice,
        CompanyInfoHelper::standardCurrency(),
        this.CurrencyId,
        this.StartDate);
    [this.SalesPrice] = ProjCostSalesPrice::findCostSalesPriceByPriceParameters(priceParams);
    this.ExchRate   = this.projTable().exchRateSales(this.StartDate,this.CurrencyId);
}

public void setResourceCategory()
{
    this.ResourceCategory = ResourceFacade::getResourceCategory(this.Resource,
    (this.StartDate ? this.StartDate : DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())));
}

public void update()
{
    ProjForecastCost this_Orig = this.orig();

    ttsbegin;

    ProjForecastPost::newPostForecastCost(this).deleteTransBudget();

    this.BudgetMap::updating();

    if ((this_Orig.Resource != this.Resource || this_Orig.ProjId != this.ProjId)
        && ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled() 
        && FeatureStateProvider::isFeatureEnabled(ProjAdvancedJournalEmployeeDimensionFeature::instance()))
    {
        this.mergeResourceDimensionDefault();
    }

    super();

    this.BudgetMap::updated(this_Orig);

    if (ProjParameters::find().ExpenseDeleteZero
       && ((this.isProjLumpSum() && this.CostPrice <=0)
           ||(!this.isProjLumpSum() && this.Qty <=0)))
    {
        this.delete();
        if (FormDataUtil::isFormDataSource(this))
        {
            // refresh and reread ProjForecastCost datasource if exists in form
            FormRun formRun = FormDataUtil::getFormDataSource(this).formRun();
            FormObjectSet formObjSet;
            for (int i = 1; i <= formRun.dataSourceCount(); i++)
            {
                if (formRun.dataSource(i).cursor().TableId == tableNum(ProjForecastCost))
                {
                    formObjSet = formRun.dataSource(i);
                    break;
                }
            }
            if (formObjSet)
            {
                formObjSet.refresh();
                formObjSet.reread();
            }
        }
    }
    else
    {
        ProjForecastPost::newPostForecastCost(this).run();
    }

    ttscommit;
}

boolean validateDelete()
{
    boolean     ok;;

    ok = super();

    if (ok)
    {
        ok  = this.checkModel();
    }

    return ok;
}

boolean validateField(FieldId _p1)
{
    ForecastModel   oldBudgetModel;
    ForecastModel   forecastModel;

    boolean ret;

    ret = super(_p1);

    switch (_p1)
    {
        case(fieldNum(ProjForecastCost,CategoryId)):
        if (this.CategoryId)
            {
                if (ProjCategory::find(this.CategoryId).CategoryType    != ProjCategoryType::Cost)
                {
                    ret = checkFailed(strFmt("@SYS54897",this.CategoryId));
                }
            }
            break;

        case(fieldNum(ProjForecastCost,ModelId)):
        if (this.ModelId != this.orig().ModelId)
            {
                oldBudgetModel  = ForecastModel::find(HeadingSub::Heading,this.orig().ModelId);
                if (oldBudgetModel.Blocked)
                {
                    ret = checkFailed(strFmt("@SYS25943",oldBudgetModel.ModelId));
                }
                forecastModel  = ForecastModel::find(HeadingSub::Heading,this.ModelId);
                if (forecastModel.Blocked)
                {
                    ret = checkFailed(strFmt("@SYS25943",forecastModel.ModelId));
                }
            }
            break;

        case fieldNum(ProjForecastCost, ExchRate):
        if (this.CurrencyId && ! this.ExchRate)
            {
                ret = checkFailed(strFmt("@SYS20792", this.CurrencyId));
            }
            break;

        case fieldNum(ProjForecastCost, ActivityNumber):
            ret = this.checkActivity();

            break;
    }

    return ret;
}

