        this.setSchedFromDateToSourceActivity();
    }
}

private boolean addErrorToList(str _msg, List _errorList)
{
    _errorList.addEnd(_msg);
    return checkFailed(_msg);
}

public boolean validateWrite()
{
    boolean ret;
    ProjOpsManagementLogger logger = ProjOpsManagementLogger::createLogger(tablestr(ProjForecastCost));
    using (var activityContext = logger.projOpsManagementActivity().projForecastUpsertContext())
    {
        activityContext.addCustomProperty(ProjOpsManagementInstrumentationActivities::ProjForecastOperation, strFmt("ProjForecastCost ValidateWrite on %1", this.RecId ? ProjOpsManagementInstrumentationActivities::ProjForecastUpdate : ProjOpsManagementInstrumentationActivities::ProjForecastInsert));
        activityContext.addCustomProperty(ProjOpsInstrumentationBaseActitivies::ProjectIdProperty, this.ProjId);

        List errorMessages = new List(Types::String);

        ret = this.BudgetMap::validateWrite();
        ret = ret && this.checkActivity();
        ret = ProjTable::find(this.ProjId).status().validateWriteBudgetCost() && ret;
        ret = (new ProjValCheckTrans()).validateMandatory(this) && ret;

        ret = ProjForecastPost::newPostForecastCost(this).runCheck() && ret;

        if (ret & this.isProjLumpSum())
        {
            if (this.Qty!=1)
            {
                ret = this.addErrorToList("@SYS107838", errorMessages);
            }

            if (this.Resource != 0)
            {
                ret = this.addErrorToList("@SYS108570", errorMessages);
            }

            if (this.ResourceCategory != 0)
            {
                ret = this.addErrorToList("@SYS108570", errorMessages);
            }
        }

        if (ret && this.isProjForecastReduced())
        {
            if (this.orig().ProjId != this.ProjId)
            {
                ret= this.addErrorToList(strFmt("@SYS113137",fieldStr(ProjForecastCost, ProjId)), errorMessages);
            }
            if (this.orig().CategoryId != this.CategoryId)
            {
                ret= this.addErrorToList(strFmt("@SYS113137",fieldStr(ProjForecastCost, CategoryId)), errorMessages);
            }
            if (this.orig().ActivityNumber != this.ActivityNumber)
            {
                ret= this.addErrorToList(strFmt("@SYS113137",fieldStr(ProjForecastCost, ActivityNumber)), errorMessages);
            }
            if (this.orig().Resource != this.Resource)
            {
                ret= this.addErrorToList(strFmt("@SYS113137",fieldStr(ProjForecastCost, Resource)), errorMessages);
            }
            if (this.orig().ResourceCategory != this.ResourceCategory)
            {
                ret= this.addErrorToList(strFmt("@SYS113137",fieldStr(ProjForecastCost, ResourceCategory)), errorMessages);
            }
            if (this.orig().ModelId != this.ModelId)
            {
                ret= this.addErrorToList(strFmt("@SYS113137",fieldStr(ProjForecastCost, ModelId)), errorMessages);
            }
        }

        if (ret)
        {
            if (this.InvoiceDate != dateNull() && this.InvoiceDate < this.StartDate)
            {
                ret = this.addErrorToList("@SYS96098", errorMessages) && ret;
            }

            if (this.EliminationDate != dateNull() && this.ModelId)
            {
                ForecastModel forecastModel = ForecastModel::find(HeadingSub::Heading,this.ModelId);
                if (forecastModel)
                {
                    ProjTable projTableLocal = ProjTable::find(this.ProjId);

                    //Check WIP is being used by model
                    switch (projTableLocal.Type)
                    {
                        case ProjType::FixedPrice :
                    if (forecastModel.ProjForecastWIPFixedPrice)
                            {
                                if (this.EliminationDate < this.StartDate)
                                {
                                    ret = this.addErrorToList("@SYS96097", errorMessages) && ret;
                                }
                            }
                            break;

                        case ProjType::TimeMaterial :
                    if (forecastModel.ProjForecastWIPTimeMaterial)
                            {
                                if (this.EliminationDate < this.StartDate)
                                {
                                    ret = this.addErrorToList("@SYS96097", errorMessages) && ret;
                                }
                            }
                            break;

                        case ProjType::Investment :
                    if (forecastModel.ProjForecastWIPInvestment)
                            {
                                if (this.EliminationDate < this.StartDate)
                                {
                                    ret = this.addErrorToList("@SYS96097", errorMessages) && ret;
                                }
                            }
                            break;

                        case ProjType::None:
                        if (forecastModel.ProjForecastWIPFixedPrice &&
                            this.EliminationDate < this.StartDate &&
                            ProjMultipleContractLinesForProjectFeatureHelper::isTransTypeIncludedByBillingMethod(projTableLocal.ProjId, projTableLocal.ProjInvoiceProjId, ProjTransType::Cost, ProjContractBillingMethod::FixedPrice))
                            {
                                ret = this.addErrorToList("@SYS96097", errorMessages) && ret;
                            }
                            else if(forecastModel.ProjForecastWIPTimeMaterial &&
                            this.EliminationDate < this.StartDate &&
                            ProjMultipleContractLinesForProjectFeatureHelper::isTransTypeIncludedByBillingMethod(projTableLocal.ProjId, projTableLocal.ProjInvoiceProjId, ProjTransType::Cost, ProjContractBillingMethod::TimeMaterial))
                            {
                                ret = this.addErrorToList("@SYS96097", errorMessages) && ret;
                            }
                            break;

                        default :
                    }
                }
            }
        }

        if (!errorMessages.empty())
        {
            ListEnumerator le = errorMessages.getEnumerator();
            while(le.moveNext())
            {
                logger.logWarning(le.current());
            }
        }
    }

    return ret;
}

static boolean exist(ProjId _projId)

{
    return _projId && (select projForecastCost
                       where projForecastCost.ProjId == _projId
                  ).RecId != 0;
}

static boolean existProjLumpSumAllocation(EstimateProjAllocationId _allocateId)
{
    ProjForecastCost   projForecastCost;

    select firstonly projForecastCost
where projForecastCost.ProjAllocateId == _allocateId;

    if (projForecastCost)
    {
        return true;
    }
    else
    {
        return false;
    }
}

public static ProjForecastCost find(ProjCostTransId _transId, boolean _forUpdate = false)
{
    ProjForecastCost    projForecastCost = null;

    projForecastCost.selectForUpdate(_forUpdate);

    if (_transId)
    {
        select projForecastCost where projForecastCost.TransId == _transId;
    }

    return projForecastCost;
}

/// <summary>
/// Finds the specified record in the <c>ProjForecastCost</c> table.
/// </summary>
/// <param name="_projId">
/// A <c>ProjId</c> value that specifies the project ID of the record to find.
/// </param>
/// <param name="_categoryId">
/// A <c>CategoryId</c> value that specofies the category ID of the record to find.
/// </param>
/// <param name="_activityNumber">
/// A <c>ActivityNumber</c> value that specifies the activity number of the record to find.
/// </param>
/// <param name="_projForecastBudgetType">
/// A <c>CategoryId</c> value that specofies the forecast budget type of the record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A <c>ProjForecastCost</c> table buffer containing the budget forecast row.
/// </returns>
public static ProjForecastCost findProjectBudgetForecast(   ProjId _projId, CategoryId _categoryId,
                                                        ProjActivityNumber _activityNumber,
                                                        ProjForecastBudgetType _projForecastBudgetType,
                                                        boolean _forUpdate = false)
{
    ProjForecastCost projForecastCost;
    if (_projId && _categoryId && _projForecastBudgetType)
    {
        projForecastCost.selectForUpdate(_forUpdate);

        select firstonly projForecastCost
    where projForecastCost.ProjId == _projId
    &&  projForecastCost.CategoryId == _categoryId
    &&  projForecastCost.ActivityNumber == _activityNumber
    &&  projForecastCost.ProjForecastBudgetType == _projForecastBudgetType;
    }

    return projForecastCost;
}

/// <summary>
/// Finds the specified record in the <c>ProjForecastCost</c> table that has expand id and allocate method fields set.
/// </summary>
/// <param name = "_projForecastCost">A <c>ProjForecastCost</c> buffer.</param>
/// <returns>true if the value is set;false otherwise.</returns>
public static boolean canCreateTransBudget(ProjForecastCost _projForecastCost)
{
    boolean ret = false;

    if ((_projForecastCost.ExpandId == 0 && _projForecastCost.AllocateMethod == BudgetAllocateMethod::None) ||
        (_projForecastCost.ExpandId != 0))
    {
        ret = true;
    }

    return ret;
}

/// <summary>
///  Updates the enddate of PSAActivitySetup to ProjForecastCost for calculation of invoicedate
/// </summary>
private void setSchedFromDateToSourceActivity()
{
    boolean isSchedFromModified = false;

    PSAActivitySetup psaActivitySetup = PSAActivitySetup::findActivityNumber(this.ActivityNumber);

    if (psaActivitySetup && psaActivitySetup.psaSchedEnd)
    {
        this.StartDate = psaActivitySetup.psaSchedEnd;
        isSchedFromModified = true;
    }

    if (isSchedFromModified)
    {
        this.initFromTransDate(false);
    }
}

public void deleteForProjIdAndModelId(ProjId _projId, ProjForecastModelId _modelId)
{
    if(ProjBudgetManagementFeatureHelper::isFeatureEnabled())
    {
        ProjForecastCost forecastCost;

        ttsBegin;

        delete_from forecastCost
            where forecastCost.ProjId == _projId 
            && forecastCost.ModelId == _modelId;

        ttsCommit;
    }
}
