/// <summary>
/// Builds a parameter for searching an inventory posting account ledger dimension for an item.
/// </summary>
/// <param name = "_accountType">An inventory account type.</param>
/// <returns>The built parameter instance.</returns>
protected InventPostingAccountItemLedgerDimensionParameters buildAccountItemLedgerDimensionParameter(InventAccountType _accountType)
{
    return InventPostingAccountItemLedgerDimensionParameters::newFromParameters(
        _accountType,
        this.ItemId,
        InventTable::find(this.ItemId).itemGroupId(),
        0,
        // If a category relation is added to the table it can be supplied here
        ProjTable::find(this.ProjId).CustAccount,
        CustTable::groupId(ProjTable::find(this.ProjId).CustAccount),
        this.TaxGroupId,
        this.inventDim());
}

