/// <summary>
///    Returns the contribution margin for an invoice line.
/// </summary>
/// <returns>
///    The contribution margin for an invoice line.
/// </returns>
display ProjContributionMargin contributionMargin()
{
    if (this.projInvoiceJour().ExchRate)
    {
        return (this.lineAmountDetailSum() - ((this.costValue()*100)/this.projInvoiceJour().ExchRate));
    }

    return 0;
}

/// <summary>
///    Returns the contribution margin for an invoice line.
/// </summary>
/// <returns>
///    The contribution margin for an invoice line.
/// </returns>
display ProjContributionRatio contributionRatio()
{
    if (this.lineAmountDetailSum())
    {
        return (this.contributionMargin() / this.lineAmountDetailSum()) * 100;
    }

    return 0;
}

