display SalesCostValue costValue()
{
    DictTable dictTableProjInvoiceItemDetail;

    dictTableProjInvoiceItemDetail = new DictTable(tableNum(ProjInvoiceItemDetail));

    if (dictTableProjInvoiceItemDetail.rights() >= AccessType::View)
    {
        return (select sum(CostValue) from projInvoiceItemDetail
                where projInvoiceItemDetail.InvoiceRefRecId == this.RecId).CostValue;
    }
    else
    {
        return 0;
    }
}

/// <summary>
///    Creates the links between the <c>ProjInvoiceItem</c> line and the agreement line.
/// </summary>
public void createAgreementLinks()
{
    AgreementLineReleasedLine   agreementLineReleasedLineInsert;
    AgreementLineReleasedLine   agreementLineReleasedLine;
    RefTableId                  refRelationType = tableNum(ProjInvoiceItem);
    RefRecId                    refRecId = this.RecId;

    if (refRecId)
    {
        insert_recordset agreementLineReleasedLineInsert (AgreementLine, ProjInvoiceItem, ReferenceRelationType)
            select AgreementLine, refRecId, refRelationType from agreementLineReleasedLine
                where agreementLineReleasedLine.SalesLineInventTransId == this.InventTransId
                   && agreementLineReleasedLine.SalesLineDataAreaId == this.DataAreaId
                   && !agreementLineReleasedLine.IsDeleted
                   && agreementLineReleasedLine.ReferenceRelationType == tableNum(SalesLine);
    }
}

