/// <summary>
/// Returns the item quantity that is sold on an invoice line.
/// </summary>
/// <returns>
/// The item quantity that is sold on an invoice line.
/// </returns>
display InventQty  inventQty()
{
    DictTable               dictTable = new DictTable(tableNum(InventTrans));
    InventTransOriginId     inventTransOriginId;

    if (dictTable.rights() < AccessType::View)
    {
        return 0.0;
    }

    inventTransOriginId = InventTransOrigin::findByInventTransId(this.InventTransId).RecId;

    return InventTransInvoice::qtySold(inventTransOriginId, this.ProjInvoiceId, this.InvoiceDate)
         + InventTransInvoice::qtyPurchased(inventTransOriginId, this.ProjInvoiceId, this.InvoiceDate);
}

