    }
}

display AmountCur lineAmountDetailSum()
{
    DictTable dictTableProjInvoiceItemDetail;

    dictTableProjInvoiceItemDetail = new DictTable(tableNum(ProjInvoiceItemDetail));

    if (dictTableProjInvoiceItemDetail.rights() >= AccessType::View)
    {
        return (select sum(LineAmount) from projInvoiceItemDetail
                where projInvoiceItemDetail.InvoiceRefRecId == this.RecId).LineAmount;
    }
    else
    {
        return 0;
    }
}

