/// <summary>
/// Calculates and updates the sales commission for an invoice line.
/// </summary>
/// <param name="_ledgerVoucher">
/// A ledger voucher.
/// </param>
/// <param name="_projProposalJour">
/// An invoice proposal.
/// </param>
/// <remarks>
/// The commission will not be recalculated if it has already been calculated as indicated by the
/// <c>CommissCalc</c> field.
/// </remarks>
public void updateCommission(LedgerVoucher _ledgerVoucher, ProjProposalJour _projProposalJour)
{
    Commission  commission;

    if (this.CommissCalc == NoYes::Yes)
    {
        return;
    }

    commission = Commission::newCommission(_ledgerVoucher, _projProposalJour, this);

    commission.run();
}

/// <summary>
///    Determines whether an invoice line exists for a given transaction.
/// </summary>
/// <param name="_transId">
///    The ID of the transaction associated with a line item.
/// </param>
/// <returns>
///    true if the line item exists; otherwise, false.
/// </returns>
public static boolean exist(ProjItemTransId _transId)
{
    if (_transId)
    {
        return (select projInvoiceItem where projInvoiceItem.ProjTransId == _transId).RecId != 0;
    }

    return false;
}

static ProjInvoiceItem findInventTransId_W(InventTransId _transId, boolean _forUpdate = false)
{
    ProjInvoiceItem    projInvoiceItem = null;

    projInvoiceItem.selectForUpdate(_forUpdate);

    if (_transId)
    {
        select projInvoiceItem where projInvoiceItem.InventTransId == _transId;
    }

    return projInvoiceItem;
}

static ProjInvoiceItem findRecId(RecId       recId,
                                 boolean     update = false )
{
    ProjInvoiceItem     projInvoiceItem;

    projInvoiceItem.selectForUpdate(update);

    select projInvoiceItem
        where projInvoiceItem.RecId== recId;

    return projInvoiceItem;
}

static ProjInvoiceItem findTransId(ProjItemTransId _transId, boolean _update = false)
{
    ProjItemTransSale       projItemTransSale;
    ProjInvoiceItemDetail   projInvoiceItemDetail;
    ProjInvoiceItem         projInvoiceItem;

    while select RecId from projItemTransSale
        where projItemTransSale.ProjTransId == _transId
    {
        projInvoiceItemDetail = ProjInvoiceItemDetail::find(projItemTransSale.RecId);
        if (projInvoiceItemDetail)
        {
            projInvoiceItem = projInvoiceItemDetail.projInvoiceItem(_update);
            break;
        }
    }

    return projInvoiceItem;
}

/// <summary>
/// Creates a new invoice line and initializes it from an invoice proposal line by using data from the
/// associated project transaction.
/// </summary>
/// <param name="_projProposalItem">
/// An invoice proposal line from which to initialize the invoice line.
/// </param>
/// <returns>
/// The new invoice line.
/// </returns>
public static ProjInvoiceItem initInvoice_Project(ProjProposalItem _projProposalItem)
{
    ProjTrans                           projTransItem   = ProjTrans::newProjProposalItem(_projProposalItem);
    ProjInvoiceItem                     projInvoiceItem;

    projInvoiceItem.TaxGroupId         = _projProposalItem.TaxGroupId;
    projInvoiceItem.TaxItemGroupId     = _projProposalItem.TaxItemGroupId;
    projInvoiceItem.OverrideSalesTax   = _projProposalItem.OverrideSalesTax;
    projInvoiceItem.TaxWriteCode       = _projProposalItem.TaxWriteCode;
    projInvoiceItem.TaxAmount          = _projProposalItem.TaxAmount;
    projInvoiceItem.ItemType           = _projProposalItem.ItemType;
    projInvoiceItem.Txt                = _projProposalItem.Txt;
    projInvoiceItem.LineAmount          = projTransItem.transTurnover();
    projInvoiceItem.psaContractLineNum  = _projProposalItem.PSAContractLineNum;
    projInvoiceItem.Qty                 = projTransItem.qty();
    projInvoiceItem.CurrencyId          = projTransItem.currencyIdSales();
    projInvoiceItem.DefaultDimension    = projTransItem.defaultDimension();
    projInvoiceItem.ProjId              = projTransItem.projId();
    projInvoiceItem.ActivityNumber      = projTransItem.activityNumber();
    projInvoiceItem.ItemId              = projTransItem.itemId();
    projInvoiceItem.CategoryId          = projTransItem.categoryId();
    projInvoiceItem.DlvDate             = projTransItem.transDate();
    projInvoiceItem.TransDate           = projTransItem.transDate();
    projInvoiceItem.PackingSlipId       = projTransItem.packingSlipId();
    projInvoiceItem.ProjAdjustRefId     = projTransItem.projAdjustRefId();
    projInvoiceItem.ProjTransId         = projTransItem.transId();
    projInvoiceItem.InventTransId       = projTransItem.inventTransId();
    projInvoiceItem.ExternalItemId      = projTransItem.externalItemId();
    projInvoiceItem.EInvoiceAccountCode = _projProposalItem.EInvoiceAccountCode;
    projInvoiceItem.OrderLineReference_NO = _projProposalItem.OrderLineReference_NO;

    return projInvoiceItem;
}

/// <summary>
///    Creates a new invoice line and initializes it from an invoice proposal line using data from the associated sales order line.
/// </summary>
/// <param name="_projProposalItem">
///    The invoice proposal line from which to initialize the invoice line.
/// </param>
/// <returns>
///    The new invoice line.
/// </returns>
#ISOCountryRegionCodes
public static ProjInvoiceItem initInvoice_SalesOrder(ProjProposalItem _projProposalItem)
{
    ProjInvoiceItem projInvoiceItem;
    SalesLine       salesLine          = SalesLine::findInventTransId(_projProposalItem.InventTransId);

    projInvoiceItem.InventTransId      = salesLine.InventTransId;
    projInvoiceItem.TaxGroupId         = _projProposalItem.TaxGroupId;
    projInvoiceItem.TaxItemGroupId     = _projProposalItem.TaxItemGroupId;
    projInvoiceItem.OverrideSalesTax   = _projProposalItem.OverrideSalesTax;
    projInvoiceItem.TaxWriteCode       = _projProposalItem.TaxWriteCode;
    projInvoiceItem.TaxAmount          = _projProposalItem.TaxAmount;
    projInvoiceItem.ItemType           = _projProposalItem.ItemType;

    projInvoiceItem.ProjTransId        = salesLine.ProjTransId;

    projInvoiceItem.Txt                = _projProposalItem.Txt;
    projInvoiceItem.Qty                = _projProposalItem.DeliverNow;
    projInvoiceItem.CurrencyId         = salesLine.CurrencyCode;

    projInvoiceItem.DefaultDimension   = salesLine.DefaultDimension;
    projInvoiceItem.ProjId             = salesLine.ProjId;
    projInvoiceItem.ActivityNumber     = salesLine.ActivityNumber;
    projInvoiceItem.ItemId             = salesLine.ItemId;
    projInvoiceItem.InventDimId        = salesLine.InventDimId;
    projInvoiceItem.CategoryId         = salesLine.ProjCategoryId;

    projInvoiceItem.LinePercent        = salesLine.LinePercent;
    projInvoiceItem.LineDisc           = salesLine.LineDisc;
    projInvoiceItem.SalesId            = salesLine.SalesId;
    projInvoiceItem.InventDimId        = salesLine.InventDimId;
    projInvoiceItem.ExternalItemId     = salesLine.ExternalItemId;
    projInvoiceItem.SalesUnit          = salesLine.SalesUnit;
    projInvoiceItem.PriceUnit          = salesLine.PriceUnit;
    projInvoiceItem.SalesMarkup        = salesLine.SalesMarkup;
    projInvoiceItem.SalesGroup         = salesLine.SalesGroup;
    projInvoiceItem.TransactionCode    = salesLine.TransactionCode;
    projInvoiceItem.Transport          = salesLine.Transport;
    projInvoiceItem.Port               = salesLine.Port;
    projInvoiceItem.StatProcId         = salesLine.StatProcId;
    projInvoiceItem.IntrastatCommodity = salesLine.IntrastatCommodity;
    projInvoiceItem.OrigCountryRegionId = salesLine.OrigCountryRegionId;
    projInvoiceItem.OrigState          = salesLine.OrigStateId;

    // <GEEU>
    projInvoiceItem.CreditNoteInternalRef_PL = salesLine.CreditNoteInternalRef_PL;
    // </GEEU>

    // <GEERU>
    if (salesLine.InventTransIdReturn && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU,#isoCZ,#isoEE,#isoHU,#isoPL,#isoLT,#isoLV]))
    {
        projInvoiceItem.RefReturnInvoiceTrans_W = ProjInvoiceItem::findInventTransId_W(salesLine.InventTransIdReturn).RecId;
    } // Credit note reference <--
    // </GEERU>

    projInvoiceItem.psaContractLineNum  = salesLine.PSAContractLineNum;
    projInvoiceItem.OrderLineReference_NO = _projProposalItem.OrderLineReference_NO;
    projInvoiceItem.EInvoiceAccountCode = _projProposalItem.EInvoiceAccountCode;

    return projInvoiceItem;
}

/// <summary>
///    Creates a new invoice line and initializes it from an invoice proposal line.
/// </summary>
/// <param name="_projProposalItem">
///    An invoice proposal line from which to initialize the invoice line.
/// </param>
/// <returns>
///    The new invoice line.
/// </returns>
#ISOCountryRegionCodes
public static ProjInvoiceItem initInvoiceFromProposale(ProjProposalItem _projProposalItem)
{
    ProjTrans       projTransItem;
    // <GEERU>
    SalesLine       salesLine;
    // </GEERU>

    // <GEEHU>
    ProjTransItemTrans  projTransItemTrans;
    // </GEEHU>

    ProjInvoiceItem projInvoiceItem;

    switch (_projProposalItem.ItemType)
    {
        case ProjItemType::Project      :
        case ProjItemType::InventJournal:
        case ProjItemType::PurchOrder   :
        case ProjItemType::VendorInvoice:
        case ProjItemType::Production   :
        case ProjItemType::ProdLine     :
        case ProjItemType::BeginningBalance     :
           projTransItem = ProjTrans::newProjItemTrans(ProjItemTrans::find(_projProposalItem.ProjTransId));
           projTransItem.parmProjItemType(_projProposalItem.ItemType);
           projInvoiceItem.TransactionCode = _projProposalItem.salesLine().TransactionCode;
           // The delivery date should be the same as the transaction date
           projInvoiceItem.DlvDate = projTransItem.transDate();

            // <GEEHU>
            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]))
            {
                projTransItemTrans = projTransItem;
                projInvoiceItem.IntrastatFulfillmentDate_HU = projTransItemTrans.intrastatFulfillmentDate_HU();
            }
            // </GEEHU>

           break;

        case ProjItemType::SalesOrder   :
           projTransItem = ProjTrans::newProjProposalItem(_projProposalItem);
           projTransItem.parmProjItemType(_projProposalItem.ItemType);
           projInvoiceItem.TransactionCode = projTransItem.transactionCode();
            // <GEEU>
            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU,#isoCZ,#isoEE,#isoHU,#isoPL,#isoLT,#isoLV]))
            {
                salesLine = _projProposalItem.salesLine();

                if (salesLine)
                {
                    projInvoiceItem.CreditNoteInternalRef_PL = salesLine.CreditNoteInternalRef_PL;

                    if (salesLine.InventTransIdReturn)
                    {
                        projInvoiceItem.RefReturnInvoiceTrans_W = ProjInvoiceItem::findInventTransId_W(salesLine.InventTransIdReturn).RecId;
                    }
                }
            }

            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]))
            {
                projInvoiceItem.IntrastatFulfillmentDate_HU = _projProposalItem.salesLine().IntrastatFulfillmentDate_HU;
            }
            // </GEEU>
           break;
    }

    projInvoiceItem.ProjTransId         = _projProposalItem.ProjTransId;
    projInvoiceItem.TaxGroupId          = _projProposalItem.TaxGroupId;
    projInvoiceItem.TaxItemGroupId      = _projProposalItem.TaxItemGroupId;
    projInvoiceItem.OverrideSalesTax    = _projProposalItem.OverrideSalesTax;
    projInvoiceItem.TaxAmount           = _projProposalItem.TaxAmount;
    projInvoiceItem.TaxWriteCode        = _projProposalItem.TaxWriteCode;
    projInvoiceItem.ItemType            = _projProposalItem.ItemType;

    projInvoiceItem.Txt                 = _projProposalItem.Txt;
    projInvoiceItem.eInvoiceAccountCode = _projProposalItem.eInvoiceAccountCode;

    projInvoiceItem.InventTransId       = projTransItem.inventTransId();
    projInvoiceItem.ItemId              = projTransItem.itemId();
    projInvoiceItem.InventDimId         = projTransItem.inventDimId();

    projInvoiceItem.Qty                 = _projProposalItem.qty();
    projInvoiceItem.CurrencyId          = projTransItem.currencyIdSales();
    projInvoiceItem.DefaultDimension    = projTransItem.defaultDimension();
    projInvoiceItem.ProjId              = projTransItem.projId();
    projInvoiceItem.ActivityNumber      = projTransItem.activityNumber();
    projInvoiceItem.CategoryId          = projTransItem.categoryId();
    projInvoiceItem.PackingSlipId       = projTransItem.packingSlipId();
    projInvoiceItem.ProjAdjustRefId     = projTransItem.projAdjustRefId();
    projInvoiceItem.SalesUnit           = projTransItem.salesUnit();
    projInvoiceItem.SalesGroup          = projTransItem.salesGroup();
    projInvoiceItem.LinePercent         = projTransItem.linePercent();
    projInvoiceItem.LineDisc            = projTransItem.lineDisc();
    projInvoiceItem.SalesId             = projTransItem.salesId();
    projInvoiceItem.ExternalItemId      = projTransItem.externalItemId();
    projInvoiceItem.PriceUnit           = projTransItem.priceUnit();
    projInvoiceItem.SalesMarkup         = projTransItem.salesMarkup();
    projInvoiceItem.Transport           = projTransItem.transport();
    projInvoiceItem.Port                = projTransItem.port();
    projInvoiceItem.StatProcId          = projTransItem.statprocId();
    projInvoiceItem.IntrastatCommodity  = projTransItem.IntrastatCommodity();
    projInvoiceItem.OrigCountryRegionId = projTransItem.origCountryRegionId();
    projInvoiceItem.OrigState           = projTransItem.origStateId();
    projInvoiceItem.TransDate           = projTransItem.transDate();

    // Init contract line number
    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        projInvoiceItem.psaContractLineNum  = _projProposalItem.psaContractLineNum;
    }

    // <GMY>
    if (TaxGSTFeatureChecker_MY::isCountryRegionMY())
    {
        projInvoiceItem.RefInvoiceID_MY      = _projProposalItem.RefInvoiceId_MY;
        projInvoiceItem.RefInvoiceDate_MY    = _projProposalItem.RefInvoiceDate_MY;
    }
    // </GMY>

    projInvoiceItem.OrderLineReference_NO = _projProposalItem.OrderLineReference_NO;

    return projInvoiceItem;
}

static void updateLineAmount(ProjInvoiceItemDetail  _projInvoiceItemDetail, boolean _reverse)
{
    ProjInvoiceItem projInvoiceItem;

    ttsbegin;

    projInvoiceItem = _projInvoiceItemDetail.projInvoiceItem(true);
    if (projInvoiceItem)
    {
        projInvoiceItem.LineAmount += (_reverse ? -_projInvoiceItemDetail.LineAmount : _projInvoiceItemDetail.LineAmount);
        projInvoiceItem.update();
    }

    ttscommit;
}

/// <summary>
/// Find related DocuRef note.
/// </summary>
/// <returns>Related DocuRef note.</returns>
public Notes getDocuRefNote()
{
    return DocuRef::findTableIdRecId(curExt(), this.TableId, this.RecId).Notes;
}

public void insert()
{
    #isoCountryRegionCodes
    super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIT]))
    {
        BaseDocument_IT::insertForProjInvoiceItem(this);
    }
}

/// <summary>
/// Gets the transaction amount MST for Standard Audit File in the European Union.
/// </summary>
/// <returns>Line transaction amount MST.</returns>
public AmountMST amountMST_W()
{
    return CurrencyExchangeHelper::amountCur2MST(this.LineAmount, this.CurrencyId, this.ExchRate());
}

