public void update()
{
    ProjParameters paramOrig = this.orig();

    if (this.projOpsFeatureWasEnabled(paramOrig))
    {
        ProjMultipleContractLinesFeatureStatus multipleContractLineFeatureStatus = this.MultipleContractLinesFeatureStatus;
        boolean projOpsFeatureStatus = this.ProjectOperationsIntegrationFeatureStatus;
        ProjOpsIntegrationLogger logger = ProjOpsIntegrationLogger::createLogger(classStr(ProjProjectOperationsIntegrationFeatureHelper));
        using (var activityContext = (ProjOpsIntegrationInstrumentationActivites::construct(logger)).activityContext())
        {
            activityContext.addCustomProperty(ProjOpsIntegrationInstrumentationActivites::MultipleContractLineFeatureEnabled, any2Str(multipleContractLineFeatureStatus));
            activityContext.addCustomProperty(ProjOpsIntegrationInstrumentationActivites::ProjOpsFeatureEnabled, any2Str(projOpsFeatureStatus));
        }

        if (projOpsFeatureStatus)
        {
            ProjParameters::resetProjParams(this);
            this.MultipleContractLinesFeatureStatus = multipleContractLineFeatureStatus;
            this.ProjectOperationsIntegrationFeatureStatus = projOpsFeatureStatus;
        }
    }

    if (this.MultipleContractLinesFeatureStatus != paramOrig.MultipleContractLinesFeatureStatus
        && ProjMultipleContractLinesFeatureStateCacheFlight::instance().isEnabled())
    {
        ProjCache::clearKeyValue(ProjCacheScope::MultipleContractLinesFeatureStatus, [strUpr(this.DataAreaId)]);
    }

    if (this.ProjectOperationsIntegrationFeatureStatus != paramOrig.ProjectOperationsIntegrationFeatureStatus
        && ProjProjectOperationsFeatureStateCacheFlight::instance().isEnabled())
    {
        ProjCache::clearKeyValue(ProjCacheScope::ProjectOperationsIntegrationFeatureStatus, [strUpr(this.DataAreaId)]);
    }

    super();

    flush ProjParameters;
}

protected static void resetProjParams(ProjParameters _params)
{
    ProjParameters defaultParams;
    defaultParams.initValue();
    buf2Buf(defaultParams, _params);
}

private boolean projOpsFeatureWasEnabled(ProjParameters _origParams)
{
    return (_origParams.MultipleContractLinesFeatureStatus == ProjMultipleContractLinesFeatureStatus::Disabled && this.MultipleContractLinesFeatureStatus != ProjMultipleContractLinesFeatureStatus::Disabled) ||
         (_origParams.ProjectOperationsIntegrationFeatureStatus == NoYes::No && this.ProjectOperationsIntegrationFeatureStatus == NoYes::Yes);
}

public boolean validateField(FieldId _fieldId)
{
    boolean ret;
    int integerValue;

    ret = super(_fieldId);

    switch (_fieldId)
    {
        case(fieldNum(ProjParameters, CostCategory)):
            if (this.CostCategory)
            {
                if (ProjCategory::find(this.CostCategory).CategoryType != ProjCategoryType::Cost)
                {
                    return checkFailed(strFmt("@SYS56468",ProjTransType::Cost));
                }
            }
            break;

        case(fieldNum(ProjParameters, RevenueCategory)):
            if (this.RevenueCategory)
            {
                if (ProjCategory::find(this.RevenueCategory).CategoryType != ProjCategoryType::Revenue)
                {
                    return checkFailed(strFmt("@SYS56468",ProjTransType::Revenue));
                }
            }
            break;

        case(fieldNum(ProjParameters, EmplCategory)):
            if (this.EmplCategory)
            {
                if (ProjCategory::find(this.EmplCategory).CategoryType != ProjCategoryType::Hour)
                {
                    return checkFailed(strFmt("@SYS56468",ProjTransType::Hour));
                }
            }
            break;

        case(fieldNum(ProjParameters, ItemCategory)):
            if (this.ItemCategory)
            {
                if (ProjCategory::find(this.ItemCategory).CategoryType != ProjCategoryType::Item)
                {
                    return checkFailed(strFmt("@SYS56468",ProjTransType::Item));
                }
            }
            break;

        case(fieldNum(ProjParameters, OnAccountCategory)):
            if (this.OnAccountCategory)
            {
                if (ProjCategory::find(this.OnAccountCategory).CategoryType != ProjCategoryType::OnAccount)
                {
                    return checkFailed(strFmt("@SYS56468", ProjTransType::OnAccount));
                }
            }
            break;

        case(fieldNum(ProjParameters, GrossMarginDecimals)):
            if (this.GrossMarginDecimals < 0)
            {
                return checkFailed("@SYS22529");
            }
            break;

        case(fieldNum(ProjParameters, ValueAddedDecimals)):
            if (this.ValueAddedDecimals < 0)
            {
                return checkFailed("@SYS22529");
            }
            break;

        case(fieldNum(ProjParameters, HourQtyDecimals)):
            if (this.HourQtyDecimals < 0 || this.HourQtyDecimals > 2)
            {
                return checkFailed("@SYS91711");
            }
            break;

        case(fieldNum(ProjParameters, UtilRateDecimals)):
            if (this.UtilRateDecimals < 0 || this.UtilRateDecimals > 2)
            {
                return checkFailed("@SYS91711");
            }
            break;

        case(fieldNum(ProjParameters, Amount1000000Decimals)):
            if (this.Amount1000000Decimals < 0 || this.Amount1000000Decimals > 2)
            {
                return checkFailed("@SYS91711");
            }
            break;

        case(fieldNum(ProjParameters, Amount1000Decimals)):
            if (this.Amount1000Decimals < 0 || this.Amount1000Decimals > 2)
            {
                return checkFailed("@SYS91711");
            }
            break;

        case(fieldNum(ProjParameters, MaxTimesheets)):
            if (this.MaxTimesheets < 0)
            {
                return checkFailed("@SYS22529");
            }
            break;

        case(fieldNum(ProjParameters, MaxTasksForAdvancedJournalLineBatch)):
            if (this.MaxTasksForAdvancedJournalLineBatch <= 0)
            {
                return checkFailed(strfmt("@SYS78465",  fieldId2name(tableNum(ProjParameters), fieldNum(ProjParameters, MaxTasksForAdvancedJournalLineBatch))));
            }
            break;

        case(fieldNum(ProjParameters, MaxBundlesForInvoiceProposalCreation)):
            if (this.MaxBundlesForInvoiceProposalCreation <= 0)
            {
                return checkFailed(strfmt("@SYS78465", fieldId2name(tableNum(ProjParameters), fieldNum(ProjParameters, MaxBundlesForInvoiceProposalCreation))));
            }
            break;

        case(fieldNum(ProjParameters, MaxTasksForEstimateBatch)):
            if (this.MaxTasksForEstimateBatch <= 0)
            {
                return checkFailed(strfmt("@SYS78465", fieldId2name(tableNum(ProjParameters), fieldNum(ProjParameters, MaxTasksForEstimateBatch))));
            }
            break;

        case(fieldNum(ProjParameters, MinTimeIncrement)):
            integerValue = real2int(this.MinTimeIncrement);

            if (integerValue != 0)
            {
                return checkFailed("@SYS345061");
            }
            break;

        case(fieldNum(ProjParameters, ProjectOperationsIntegrationFeatureStatus)):
            if (this.ProjectOperationsIntegrationFeatureStatus && 
                !ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled()) // Checks the current company toggle
            {
                return checkFailed("@Proj:ProjectOperationsToggleDependencyErrorMessage");
            }
            else if (!this.ProjectOperationsIntegrationFeatureStatus && this.orig().ProjectOperationsIntegrationFeatureStatus && this.MultipleContractLinesFeatureStatus != ProjMultipleContractLinesFeatureStatus::Disabled)
            {
                return checkFailed("@Proj:ProjectOperationsParamDisableError");
            }

            // Cannot enable or disable the toggle, if there are any integrated CE project or contract.
            if (ProjProjectOperationsIntegrationFeatureHelper::existProjectOrContractFromCE())
            {
                return checkFailed("@Proj:ProjectOperationsToggleErrorMessage");
            }
            break;

        case fieldNum(ProjParameters, ProjTransCFOPOtherState_BR) :
            if (BrazilParameters::isEnabled() && this.ProjTransCFOPOtherState_BR != 0)
            {
                if (CFOPTable_BR::find(this.ProjTransCFOPOtherState_BR).CustVendLocation != CustVendLocation_BR::OtherState)
                {
                    ret = checkFailed(strFmt("@GLS63432",
                                      CFOPTable_BR::find(this.ProjTransCFOPOtherState_BR).cfopId,
                                      CustVendLocation_BR::OtherState));
                }
                else if (CFOPTable_BR::find(this.ProjTransCFOPOtherState_BR).Direction != FiscalDocDirection_BR::Outgoing)
                {
                    ret = checkFailed(strFmt("@GLS63432",
                                      CFOPTable_BR::find(this.ProjTransCFOPOtherState_BR).cfopId,
                                      FiscalDocDirection_BR::Outgoing));
                }
                else
                {
                    ret = this.cfopExistsInCFOPMatrix(this.ProjTransCFOPOtherState_BR, CustVendLocation_BR::OtherState);
                }
            }
            break;

        case fieldNum(ProjParameters, ProjTransCFOPOutsideCountry_BR) :
            if (BrazilParameters::isEnabled() && this.ProjTransCFOPOutsideCountry_BR != 0)
            {
                if (CFOPTable_BR::find(this.ProjTransCFOPOutsideCountry_BR).CustVendLocation != CustVendLocation_BR::OutsideCountry)
                {
                    ret = checkFailed(strFmt("@GLS63432",
                                      CFOPTable_BR::find(this.ProjTransCFOPOutsideCountry_BR).cfopId,
                                      CustVendLocation_BR::OutsideCountry));
                }
                else if (CFOPTable_BR::find(this.ProjTransCFOPOutsideCountry_BR).Direction != FiscalDocDirection_BR::Outgoing)
                {
                    ret = checkFailed(strFmt("@GLS63432",
                                      CFOPTable_BR::find(this.ProjTransCFOPOutsideCountry_BR).cfopId,
                                      FiscalDocDirection_BR::Outgoing));
                }
                else
                {
                    ret = this.cfopExistsInCFOPMatrix(this.ProjTransCFOPOutsideCountry_BR, CustVendLocation_BR::OutsideCountry);
                }
            }
            break;

        case fieldNum(ProjParameters, ProjTransCFOPSameState_BR) :
            if (BrazilParameters::isEnabled() && this.ProjTransCFOPSameState_BR != 0)
            {
                if (CFOPTable_BR::find(this.ProjTransCFOPSameState_BR).CustVendLocation != CustVendLocation_BR::SameState)
                {
                    ret = checkFailed(strFmt("@GLS63432",
                                      CFOPTable_BR::find(this.ProjTransCFOPSameState_BR).cfopId,
                                      CustVendLocation_BR::SameState));
                }
                else if (CFOPTable_BR::find(this.ProjTransCFOPSameState_BR).Direction != FiscalDocDirection_BR::Outgoing)
                {
                    ret = checkFailed(strFmt("@GLS63432",
                                      CFOPTable_BR::find(this.ProjTransCFOPSameState_BR).cfopId,
                                      FiscalDocDirection_BR::Outgoing));
                }
                else
                {
                    ret = this.cfopExistsInCFOPMatrix(this.ProjTransCFOPSameState_BR, CustVendLocation_BR::SameState);
                }
            }
            break;
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret = true;

    // Prevent a record from being writen to the ProjParameters table if an entry does not exists in the ApproverGroupId field when Approval is set to active
    if (this.ApproveActive == NoYes::Yes && this.ApproveGroupId == '')
    {
        // A user group must be selected in order to make approval active.
        ret = checkFailed("@SYS90741");
    }

    // Prevent vendor tax option from being splitting tax by retention amount when retention is calculated based on gross amount
    if (ret && this.VendorRetentionOptions == ProjDefaultRetentionOptions::BasedOnGrossAmount && this.VendorTaxOptions == ProjDefaultTaxOptions::SplittingTaxByRetainedAmount)
    {
        ret = checkFailed(strFmt("@Proj:VendorTaxOptionsError", this.VendorTaxOptions, this.VendorRetentionOptions));
    }

    // Prevent customer tax option from being splitting tax by retention amount when retention is calculated based on gross amount 
    if (ret && this.CustomerRetentionOptions == ProjDefaultRetentionOptions::BasedOnGrossAmount && this.CustomerTaxOptions == ProjDefaultTaxOptions::SplittingTaxByRetainedAmount)
    {
        ret = checkFailed(strFmt("@Proj:CustomerTaxOptionsError", this.CustomerTaxOptions, this.CustomerRetentionOptions));
    }

    if (ret && this.InvoiceProposalLineEditPerf == NoYes::Yes
        && ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled())
    {
        ret = checkFailed("@Proj:DisableAutomaticRefreshOfSummariesValidationError");
    }

    if (ret && this.ContractLineBasedRevenueRecognition == NoYes::Yes
        && !ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled())
    {
        ret = checkFailed(strFmt("@Proj:ProjContractLineBasedRevRecEnableError", curExt()));
    }
    else if (ret && this.ContractLineBasedRevenueRecognition == NoYes::No
        && this.orig().ContractLineBasedRevenueRecognition == NoYes::Yes)
    {
        ret = checkFailed("@Proj:ProjContractLineBasedRevRecDisableError");
    }

    return ret && super();
}

