public static boolean adjustable(ProjTransStatus _status)
{
    boolean adjustable;
    ProjParameters projParameters;

    projParameters= ProjParameters::find();
    switch (_status)
    {
        case ProjTransStatus::Posted:

            adjustable=projParameters.PostedStatus;
            break;

        case ProjTransStatus::InvoiceProposal:

            adjustable=projParameters.InvoiceProposalStatus;
            break;

        case ProjTransStatus::Invoiced:

            adjustable=projParameters.InvoicedStatus;
            break;

        case ProjTransStatus::Estimated:

            adjustable=projParameters.EstimatedStatus;
            break;

        case ProjTransStatus::Eliminated:

            adjustable=projParameters.EliminatedStatus;
            break;

        default:
            adjustable =false;
    }
    return adjustable;
}

