public static void createDocuRef(
    DocuRestriction _restriction,
    Notes           _notes,
    Common          _common)
{
    DocuRef docuRef;

    if (_notes)
    {
        docuRef.clear();
        docuRef.TypeId = ProjParameters::docuTypeId();
        docuRef.Restriction = _restriction;
        switch (_restriction)
        {
            case DocuRestriction::Internal:
                docuRef.Name = ProjParameters::internalCommentDescription();
                break;

            case DocuRestriction::External:
                docuRef.Name = ProjParameters::externalCommentDescription();
                break;
        }

        docuRef.Notes        = _notes;
        docuRef.RefCompanyId = _common.DataAreaId;
        docuRef.RefTableId   = _common.TableId;
        docuRef.RefRecId     = _common.RecId;
        docuRef.doInsert();
    }
}

public static void createDocuType()
{
    #define.DocuNoteClassId(119)

    DocuType        type;

    if (!DocuType::exist(ProjParameters::docuTypeId()))
    {
        type.TypeId         = ProjParameters::docuTypeId();
        type.TypeGroup      = DocuTypeGroup::Note;
        type.ActionClassId  = #DocuNoteClassId;
        type.FilePlace      = DocuFilePlace::Database;
        type.ArchivePath    = '';
        type.doInsert();
    }
}

