/// <summary>
/// Retrieves the date of the end of the week.
/// </summary>
/// <param name="_transDate">
/// The date for which the end of the week date information is desired.
/// </param>
/// <returns>
/// The date of the end of the week.
/// </returns>
/// <remarks>
/// The first day of the week is determined by the <c>firsDayOfWeek</c> function that is based on AOS
/// locale.If the week starts on Sunday, the end of the week will be next Saturday similarly if the
/// week starts on Monday the end of the week will be next Sunday The logic can be simplified into a
/// single if else block but might be difficult to follow.
/// </remarks>
public static date dateEndOfWeekOnAOS(date _transDate)
{
    #TimeConstants
    date dateEndOfWeek;

    // firstDayOfWeekAOS returns a number 0 to 6, Monday = 0 and Sunday = 6
    int firstDay = ProjParameters::firstDayOfWeekAOS();

    // dayofwk returns a number 1 to 7, Monday = 1 and Sunday = 7
    int dayOfWeek = dayOfWk(_transdate);
    if (firstDay > 0)
    {
        if (firstDay > (dayOfWeek - 1))
        {
            dateEndOfWeek = _transDate + firstday - dayOfWeek;
        }
        else
        {
            dateEndOfWeek = _transDate + #daysPerWeek - (dayOfWeek - firstDay) ;
        }
    }
    else
    {
        dateEndOfWeek = _transDate + #daysPerWeek - dayOfWeek;
    }

    return dateEndOfWeek;
}

