}

/// <summary>
/// Gets default project category based on the given transaction type.
/// </summary>
/// <param name="_transactionType">
/// The type of transaction
/// </param>
/// <returns>
/// The <c>ProjCategoryId</c> value of the default category.
/// </returns>
public static ProjCategoryId defaultProjCategoryBasedOnSMATransactionType(SMATransactionType _transactionType)
{
    ProjCategoryId  projCategoryId;
    ProjParameters parameters = ProjParameters::find();

    switch (_transactionType)
    {
        case SMATransactionType::Hour:
            projCategoryId = parameters.EmplCategory;
            break;
        case SMATransactionType::Expense:
            projCategoryId = parameters.CostCategory;
            break;
        case SMATransactionType::Item:
            projCategoryId = parameters.ItemCategory;
            break;
        case SMATransactionType::Fee:
            projCategoryId = parameters.RevenueCategory;
            break;
        default:
            projCategoryId = ProjParameters::projCategoryForDefaultSMATransactionType(_transactionType);
    }

    return projCategoryId;
}

