/// </returns>
[Replaceable] 
public static ProjCategoryId projCategoryForDefaultSMATransactionType(SMATransactionType _transactionType)
{
    ProjCategoryId projCategoryId;        
    error ("@SYS83964");
    return projCategoryId;
}

