public static ProjParameters find(boolean _forupdate = false)
{
    ProjParameters parameter = null;

    parameter.selectForUpdate(_forupdate);

    select firstonly parameter
        index Key
        where parameter.Key == 0;

    if (!parameter && !parameter.isTmp())
    {
        Company::createParameter(parameter);
        PrintMgmt::createDefaultData(PrintMgmtHierarchyType::Project);
        ProjStatusTypeRule::createDefaultData();
    }

    return parameter;
}

/// <summary>
/// return the project parameter for another LE
/// </summary>
/// <param name="_dataArea">
/// the dataArea where to find the proj parameter
/// </param>
/// <returns>
/// The record in specified dataArea
/// </returns>
public static ProjParameters findByDataArea(DataAreaId _dataArea)
{
    ProjParameters  projParameters;

    select firstonly crossCompany * from projParameters
            where projParameters.DataAreaId == _dataArea;

    return projParameters;
}

/// <summary>
/// Retrieves the first day of the week using AOS locale.
/// </summary>
/// <returns>
/// An integer value that indicates the day of the week.
/// </returns>
/// <remarks>
/// The day of the week is represented by increasing integer values. Starting with 0 for Sunday and
/// ending with 6 for Saturday.This differs from the global <c>firstDayOfWeek</c> function.
/// <c>Global::firstDayOfWeek</c> is getting the locale of the client.
/// </remarks>
public static int firstDayOfWeekAOS()
{
    SysGlobalCache  cache   = classfactory.globalCache();
    System.Globalization.DateTimeFormatInfo dateTimeFormatInfo;
    System.DayOfWeek dayOfWeek;
    int aosFirstDayOfWeek;

    if (cache.isSet(tableStr(ProjParameters), funcName()))
    {
        aosFirstDayOfWeek = cache.get(tableStr(ProjParameters), funcName());
    }
    else
    {
        dateTimeFormatInfo = System.Globalization.DateTimeFormatInfo::get_CurrentInfo();
        dayOfWeek = dateTimeFormatInfo.get_FirstDayOfWeek();
        aosFirstDayOfWeek = System.Convert::ToInt32(dayOfWeek);
        cache.set(tableStr(ProjParameters), funcName(),aosFirstDayOfWeek);
    }

    return aosFirstDayOfWeek;
}

public static ProjGrossMarginPct grossMarginPct(
    AmountMST  _totalCost,
    AmountMST  _revenue,
    AmountMST  _margin)
{
    switch (ProjParameters::find().GrossMarginDisplay)
    {
        case ProjGrossMarginDisplay::MarkupFactor:
            if (_totalCost)
            {
                return (_revenue / _totalCost);
            }
            break;

        case ProjGrossMarginDisplay::ContributionMargin:
            if (_revenue)
            {
                return (_margin * 100 / _revenue);
            }
            break;

        case ProjGrossMarginDisplay::MarkupPct:
            if (_totalCost)
            {
                return (_margin * 100 / _totalCost);
            }
            break;
    }

    return 0;
}

