public static ProjProdPostingType prodPostingType()
{
    ProjProdPostingType ret;
    ProjParameters parameter = ProjParameters::find();

    switch (parameter.ProdPostingType)
    {
        case ProjProdPostingTypeSetup::FinishedItemMethod:
            ret = ProjProdPostingType::FinishedItemMethod;
            break;

        case ProjProdPostingTypeSetup::ConsumedMethod:
            if (CostSheetTable::hasCostType(CostPriceType::COGM))
            {
                ret = ProjProdPostingType::FinishedItemMethod;
            }
            else
            {
                ret = ProjProdPostingType::ConsumedMethod;
            }
            break;

        default:
            ret = ProjProdPostingType::FinishedItemMethod;
    }

    return ret;
}

public static boolean projAdjustmentEnabled(ProjTransStatus _transStatus)
{
    boolean         ret;
    ProjParameters  parameters;

    parameters = ProjParameters::find();

    switch (_transStatus)
    {
        case ProjTransStatus::Posted :
            ret = parameters.PostedStatus;
            break;

        case ProjTransStatus::InvoiceProposal :
            ret = parameters.InvoiceProposalStatus;
            break;

        case ProjTransStatus::Invoiced, ProjTransStatus::MarkedCreditnote, ProjTransStatus::CreditnoteProposal :
            ret = parameters.InvoicedStatus;
            break;

        case ProjTransStatus::Estimated :
            ret = parameters.EstimatedStatus;
            break;

        case ProjTransStatus::Eliminated :
            ret = parameters.EliminatedStatus;
            break;
    }

    return ret;
}

/// <summary>
///     Returns the <c>Query</c> object used to lookup
/// </summary>
/// <param name="_location">
///     The location
/// </param>
/// <returns>
///     The <c>Query</c> object
/// </returns>
public static Query projTransCFOPLookupQuery_BR(CustVendLocation_BR _location)
{
    QueryBuildDataSource    qbds;
    Query                   query;

    query = new Query();

    qbds = query.addDataSource(tableNum(CFOPTable_BR));
    qbds.addRange(fieldNum(CFOPTable_BR, Direction)).value(queryValue(FiscalDocDirection_BR::Outgoing));
    qbds.addRange(fieldNum(CFOPTable_BR, CustVendLocation)).value(queryValue(_location));

    qbds = qbds.addDataSource(tableNum(CFOPMatrix_BR));
    qbds.relations(true);
    qbds.joinMode(JoinMode::ExistsJoin);
    qbds.addRange(fieldNum(CFOPMatrix_BR, Transactiontype)).value(queryValue(SalesPurchTrntype_BR::Sales));

    return query;
}

public static TaxGroup taxGroupInvoice(ProjId _projId)
{
    ProjTable           projTable;
    ProjInvoiceTable    projInvoiceTable;
    TaxGroup            taxGroup;

    if (_projId)
    {
        projTable = ProjTable::find(_projId);
    }

    ProjTaxGroupMethod projTaxGroupdMethod = ProjParameters::find().TaxGroupMethod;

    if ((projTaxGroupdMethod == ProjTaxGroupMethod::InvoiceProject) || (projTaxGroupdMethod == ProjTaxGroupMethod::Search))
    {
        if (projTable.ProjInvoiceProjId)
        {
            projInvoiceTable = ProjInvoiceTable::find(projTable.ProjInvoiceProjId);
        }
    }

    switch (projTaxGroupdMethod)
    {
        case ProjTaxGroupMethod::Project :
            taxGroup = projTable.TaxGroupId;
            break;

        case ProjTaxGroupMethod::InvoiceProject :
            taxGroup = projInvoiceTable.TaxGroupId;
            break;

        case ProjTaxGroupMethod::AccountReceivable:
            taxGroup = CustTable::find(projTable.CustAccount).TaxGroup;
            break;

        case ProjTaxGroupMethod::Search :
            // Project
            if (projTable.TaxGroupId)
            {
                taxGroup = projTable.TaxGroupId;
                break;
            }

            // Contract
            if (projInvoiceTable.TaxGroupId)
            {
                taxGroup = projInvoiceTable.TaxGroupId;
                break;
            }

            taxGroup = CustTable::find(projTable.CustAccount).TaxGroup;
    }

    return taxGroup;
}

public static TaxGroup taxItemGroupItem(ProjCategoryId _categoryId, ItemId _itemId)
{
    TaxGroup    taxGroup;

    switch (ProjParameters::find().TaxItemGroupMethod)
    {
        case ProjTaxItemGroupMethod::ItemNumber:
            if (_itemId)
            {
                taxGroup = InventTable::find(_itemId).salesTaxItemGroupId();
            }
            break;
        case ProjTaxItemGroupMethod::Category:
            taxGroup = ProjCategory::find(_categoryId).TaxItemGroupId;
            break;
    }

    return taxGroup;
}

