/// <summary>
/// Checks if an activity is a parent and then verify if parent activity selection is allowed for transaction entries.
/// </summary>
/// <param name = "_activityNumber">Activity number that is checked.</param>
/// <returns></returns>
public static boolean allowParentActivitySelection(ProjActivityNumber _activityNumber)
{
    boolean ret = true;

    // check if BlockParentActivitySelection is true
    if (ProjParameters::find().BlockParentActivitySelection &&
        HierarchyTreeTable::isSummaryActivity(_activityNumber))
    {
        ret = false;
    }

    return ret;
}

/// <summary>
/// Max tasks spawned when posting proj advanced journal lines using batch.
/// Must be > 0.
/// </summary>
/// <returns>
/// Max number of tasks for parallel processing of proj advanced journal lines.
/// </returns>
public int getMaxTasksForAdvancedJournalLineBatch()
{
    const int MaxTasksToSpawn = 4;
    return (this.MaxTasksForAdvancedJournalLineBatch > 0)? this.MaxTasksForAdvancedJournalLineBatch : MaxTasksToSpawn;
}

/// <summary>
/// The maximum number of subtasks which be executed when generating invoice proposals.
/// Must be > 0.
/// For example, if this is 10 and there are 1000 unique contracts, it will spawn
/// 10 subtasks with 100 subtasks each.
/// </summary>
/// <returns>
/// Number of task subtasks to execute invoice proposal batch in parallel.
/// </returns>
public int getMaxBundlesForInvoiceProposalCreation()
{
    return this.MaxBundlesForInvoiceProposalCreation > 0 ? this.MaxBundlesForInvoiceProposalCreation : ProjConstants::NumOfBatchSubTasks;
}

/// <summary>
/// The maximum number of subtasks which be executed when creating estimate.
/// It must be > 0.
/// </summary>
/// <returns>
/// Number of task subtasks to execute estimate creation batch in parallel.
/// </returns>
public int getMaxTasksForEstimateBatch()
{
    return this.MaxTasksForEstimateBatch > 0 ? this.MaxTasksForEstimateBatch : ProjConstants::NumOfBatchSubTasks;
}

