Method.Source:
/// <summary>
///  Returns string which contains currency ISO Code and string code.
/// </summary>
/// <param name="_currencyCode">
/// currency code to find  the ISO code
/// </param>
/// <returns>
///  String value.
/// </returns>
display public Description currencyNameCode(CurrencyCode _currencyCode = this.CurrencyCode)
{
    Description     ret;
    Currency        currency;
    ISOCurrencyCode isoCurrencyCode;

    if (_currencyCode != CompanyInfoHelper::standardCurrency())
    {
        currency = Currency::find(_currencyCode);
        isoCurrencyCode = ISOCurrencyCode::find(currency.CurrencyCodeISO);

        ret = strFmt("@SYS26868", currency.Txt, isoCurrencyCode.ISOCurrencyCodeNum);
    }

    return ret;
}

/// <summary>
/// Determines facture module depending on current record transaction type
/// </summary>
/// <returns>
/// <c>FactureModule_RU</c> value
/// </returns>
public FactureModule_RU factureModule()
{
    FactureModule_RU    ret;

    switch (this.TransType)
    {
        case SalesPurchBookTransType_RU::PrepaymentStorno :
            ret = FactureModule_RU::Cust;
            break;
        case SalesPurchBookTransType_RU::AdvanceReport    :
            ret = FactureModule_RU::Empl;
            break;
        case SalesPurchBookTransType_RU::TaxCorrection    :
            ret = FactureJour_RU::findTaxCorrection(this.FactureId, FactureBook_RU::Purch).Module;
            break;
        default                                           :
            ret = FactureModule_RU::Vend;
    }

    return ret;
}

/// <summary>
/// Checks if current record is contractor's work storno
/// </summary>
/// <returns>
/// If current record is contractor's work storno returns true; otherwise false
/// </returns>
public boolean isContractorsWorkStorno()
{
    return this.TransType == SalesPurchBookTransType_RU::Invoice &&
           this.AmountInclVAT < 0                                &&
           ! this.ReverseTrans                                   &&
           ! this.PaymentDate                                    &&
           ! this.PaymentRecIdRef                                &&
           ! this.SettlementDate;
}

