/// <summary>
///  Sets or Gets the value in the <c>LineDiscCalculation_PL</c> field on the <c>PurchParameters</c> record.
/// </summary>
/// <param name="_set">
///  A <c>Boolean</c> value determining whether the passed in value should be stored.
/// </param>
/// <param name="_calcType">
///  A value from the <c>PlLineDiscCalcType</c> enumeration.
/// </param>
/// <returns>
///  A value from the <c>PlLineDiscCalcType</c> enumeration.
/// </returns>
edit PlLineDiscCalcType editSalesLineDiscCalcType(boolean _set, PlLineDiscCalcType _calcType)
{
    SalesParameters     salesParameters;

    if (_set)
    {
        ttsbegin;

        salesParameters = SalesParameters::find(true);

        if (_calcType != salesParameters.LineDiscCalculation_PL)
        {
            salesParameters.LineDiscCalculation_PL = _calcType;
            salesParameters.update();
        }

        ttscommit;
    }
    else
    {
        salesParameters = SalesParameters::find();
    }

    return salesParameters.LineDiscCalculation_PL;
}

public void initValue()
{
    const int DefaultOrderBatchTaskSizeLines = 0;
    const int DefaultOrderBatchTaskSize = 5;

    super();

    this.PromptQty = NoYes::Yes;
    this.OrderBatchTaskSize = DefaultOrderBatchTaskSize;
    this.OrderBatchTaskSizeLines = DefaultOrderBatchTaskSizeLines;
    this.DecimalPrecisionCheckQty = NoYes::Yes;
}

public PurchUpdate recommendedSpecQty(DocumentStatus _documentStatus)
{
    PurchUpdate specQty;

    switch (_documentStatus)
    {
        case DocumentStatus::Invoice :
            specQty = PurchUpdate::PackingSlip;
            break;

        default:
            specQty = PurchUpdate::All;
            break;
    }

    return specQty;
}

/// <summary>
/// Splits the delivery information.
/// </summary>
/// <param name="_documentStatus">
/// The document status.
/// </param>
/// <returns>
/// Returns true if the split succeed.
/// </returns>
public boolean splitDeliveryInformation_IN(DocumentStatus _documentStatus)
{
    boolean splitDeliveryInformation;

    switch (_documentStatus)
    {
        case DocumentStatus::PackingSlip :
            splitDeliveryInformation = this.SplitDeliveryPackingSlip_IN;
            break;

        case DocumentStatus::Invoice :
            splitDeliveryInformation = this.SplitDeliveryInvoice_IN;
            break;

        default:
            splitDeliveryInformation = false;
            break;
    }
    return splitDeliveryInformation;
}

public static container  allowedNumSeqGroup()
{
    // <GEERU>
    #ISOCountryRegionCodes
    boolean     countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);

    if (countryRegion_RU)
    {
        return [PurchParameters::numRefPurchPackingSlipId().NumberSequenceDatatype,
                PurchParameters::numRefPurchInvoiceId().NumberSequenceDatatype,
                PurchParameters::numRefPurchCreditNoteId().NumberSequenceDatatype,
                PurchParameters::numRefPurchPackingSlipVoucher().NumberSequenceDatatype,
                PurchParameters::numRefPurchInvoiceVoucher().NumberSequenceDatatype,
                PurchParameters::numRefPurchCreditNoteVoucher().NumberSequenceDatatype,
                PurchParameters::numRefPurchInvoice4PaymId_RU().NumberSequenceDatatype,
                PurchParameters::numRefPurchCorrectInvoiceVoucher_RU().NumberSequenceDatatype,
                PurchParameters::numRefPurchCorrectCreditNoteVoucher_RU().NumberSequenceDatatype];
    }
    // </GEERU>

    return [PurchParameters::numRefPurchPackingSlipId().NumberSequenceDatatype,
            PurchParameters::numRefPurchInvoiceId().NumberSequenceDatatype,
            PurchParameters::numRefPurchCreditNoteId().NumberSequenceDatatype,
            PurchParameters::numRefPurchPackingSlipVoucher().NumberSequenceDatatype,
            PurchParameters::numRefPurchInvoiceVoucher().NumberSequenceDatatype,
            PurchParameters::numRefPurchCreditNoteVoucher().NumberSequenceDatatype ];
}

