/// <summary>
/// Determines if date of exchange rate is based on document date.
/// </summary>
/// <param name="_taxGroup">
/// <c>TaxGroup</c> object.
/// </param>
/// <returns>
/// true if date of exchange rate is based on document date; Otherwise, false.
/// </returns>
public static boolean dateOfExchRateIsDocumentDate(TaxGroup _taxGroup)
{
    DateOfExchRate_W dateOfExchRate = (select purchParameters where purchParameters.Key == 0).DateOfExchRate_W;

    return dateOfExchRate == DateOfExchRate_W::DocumentDate ||
            (dateOfExchRate == DateOfExchRate_W::DocumentDateEU && TaxGroupHeading::find(_taxGroup).euTrade_W);
}

public static boolean exist()
{
    return (select firstonly RecId from purchParameters).RecId != 0;
}

public static PurchParameters  find(boolean _forupdate = false)
{
    PurchParameters parameter;

    // Race conditions can occur during upgrade, when the parameter record is created.
    // Eventhough Company::createParameter(parameter) is handling DuplicateKeyException by it's own,
    // this try/catch is needed to catch DuplicateKeyException from remaing method calls.
    try
    {
        if (_forupdate)
            parameter.selectForUpdate(_forupdate);

        select firstonly parameter
            index Key
            where parameter.Key == 0;

        if (!parameter && !parameter.isTmp())
        {
            Company::createParameter(parameter);
            PurchSummaryParameters::initiateAll();
            PrintMgmt::createDefaultData(PrintMgmtHierarchyType::Purch);
            PurchTable2LineParameters::initiate();
        }
    }
    catch (Exception::DuplicateKeyException)
    {
        retry;
    }
    return parameter;
}

