/// <summary>
/// Determines whether the specified field is valid.
/// </summary>
/// <param name="_fieldId">
/// The field ID of the field to validate.
/// </param>
/// <returns>
/// true if the field is valid; otherwise, false.
/// </returns>
public boolean validateField(FieldId _fieldId)
{
    boolean ok = super(_fieldId);

    if (ok)
    {
        switch (_fieldId)
        {
            case fieldNum(PurchParameters, ProductReceiptCharges):
                if (this.ProductReceiptCharges == NoYes::Yes
                    && VendParameters::find().PostPackingSlip == NoYes::No)
                {
                    warning("@SCM:ProductReceiptChargesParameterWarning");
                }
                break;
        }
    }
    return ok;
}

/// <summary>
/// Determines the access level for invoiced purchase orders.
/// </summary>
/// <param name = "_treatInvoicedOrderUpdateWarningAsError">
/// A boolean value that indicates whether the "Warning" access level to invoiced orders must be treated as "Locked".
/// </param>
/// <returns>
/// The access level for invoiced purchase orders.
/// </returns>
public SalesPurchAccesLevel accesLevelInvoiced(boolean _treatInvoicedOrderUpdateWarningAsError)
{
    if (_treatInvoicedOrderUpdateWarningAsError && this.AccesLevelInvoiced == SalesPurchAccesLevel::Warning)
    {
        return SalesPurchAccesLevel::Locked;
    }

    return this.AccesLevelInvoiced;
}

internal boolean isDeliveryInformationCreateAllowed(TradeLineDlvType _tradeLineDlvType)
{
    boolean ret = false;

    switch (_tradeLineDlvType)
    {
        case TradeLineDlvType::DropShip:
            ret = this.DeliveryInfoSyncOnCreateDirectDelivery;
            break;

        case TradeLineDlvType::None:
            ret = this.DeliveryInfoSyncOnCreateStock;
            break;
    }

    return ret;
}

internal boolean isDeliveryInformationUpdateAllowed(TradeLineDlvType _tradeLineDlvType)
{
    boolean ret = false;

    switch (_tradeLineDlvType)
    {
        case TradeLineDlvType::DropShip:
            ret = this.DeliveryInfoSyncOnUpdateDirectDelivery;
            break;

        case TradeLineDlvType::None:
            ret = this.DeliveryInfoSyncOnUpdateStock;
            break;
    }

    return ret;
}

internal boolean isDirectDeliveryInformationCreateOrUpdateAllowed(Common _common, FieldId _fieldId, boolean _create)
{
    return (_create
                && this.DeliveryInfoSyncOnCreateDirectDelivery)
                || (_common.fieldChanged(_fieldId)
                    && this.DeliveryInfoSyncOnUpdateDirectDelivery);
}

internal boolean isStockDeliveryInformationCreateOrUpdateAllowed(Common _common, FieldId _fieldId, boolean _create)
{
    return (_create
                && this.DeliveryInfoSyncOnCreateStock)
                || (_common.fieldChanged(_fieldId)
                    && this.DeliveryInfoSyncOnUpdateStock);
}
