/// <summary>
/// Applies default value to a field.
/// </summary>
/// <param name="_fieldId">
/// The field ID.
/// </param>
/// <param name="_purchParmTable">
/// An instance of the <c>PurchParmTable</c> Table, used to track state; optional.
/// </param>
/// <param name="_purchTable">
/// A <c>PurchTable</c> table record, used to copy values from; optional.
/// </param>
/// <param name="_ledgerJournalTrans">
///    A <c>LedgerJournalTrans</c> record from which values are copied; optional.
/// </param>
public void defaultField(FieldId _fieldId, PurchTable _purchTable = null, LedgerJournalTrans _ledgerJournalTrans = null)
{
